import React, { useState } from 'react';

const ProfessionalCalendar = ({ holidays = [], onAddHoliday, onDeleteHoliday, theme = 'light' }) => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [editingHoliday, setEditingHoliday] = useState(null);
  const [holidayForm, setHolidayForm] = useState({ name: '', date: '' });

  const months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
  const days = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

  const navigateMonth = (direction) => {
    const newDate = new Date(currentDate);
    newDate.setMonth(currentDate.getMonth() + direction);
    setCurrentDate(newDate);
  };

  const getHolidaysForDate = (day) => {
    const date = new Date(currentDate.getFullYear(), currentDate.getMonth(), day);
    return holidays.filter(h => new Date(h.date).toDateString() === date.toDateString());
  };

  const handleDateClick = (day) => {
    const date = new Date(currentDate.getFullYear(), currentDate.getMonth(), day);
    setSelectedDate(date);
    setShowModal(true);
    setHolidayForm({ name: '', date: date.toISOString().split('T')[0] });
  };

  const handleSaveHoliday = async () => {
    if (!holidayForm.name.trim()) return;

    const holidayData = {
      name: holidayForm.name,
      date: holidayForm.date,
      year: new Date(holidayForm.date).getFullYear()
    };

    if (editingHoliday) {
      try {
        const response = await fetch(`http://localhost:5002/api/holidays/${editingHoliday._id}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(holidayData)
        });
        if (response.ok && onAddHoliday) onAddHoliday();
      } catch (error) {
        console.error('Error updating holiday:', error);
      }
    } else {
      if (onAddHoliday) onAddHoliday(holidayData);
    }

    setShowModal(false);
    setEditingHoliday(null);
    setHolidayForm({ name: '', date: '' });
  };

  const handleEditHoliday = (holiday) => {
    setEditingHoliday(holiday);
    setHolidayForm({
      name: holiday.name,
      date: new Date(holiday.date).toISOString().split('T')[0]
    });
    setShowModal(true);
  };

  const handleDeleteHoliday = (holidayId) => {
    if (window.confirm('Delete this holiday?')) {
      onDeleteHoliday(holidayId);
    }
  };

  const renderCalendarDays = () => {
    const daysInMonth = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0).getDate();
    const firstDay = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1).getDay();
    const days = [];
    const today = new Date();

    // Empty cells
    for (let i = 0; i < firstDay; i++) {
      days.push(<div key={`empty-${i}`} className="h-24"></div>);
    }

    // Days
    for (let day = 1; day <= daysInMonth; day++) {
      const date = new Date(currentDate.getFullYear(), currentDate.getMonth(), day);
      const dayHolidays = getHolidaysForDate(day);
      const isToday = date.toDateString() === today.toDateString();

      days.push(
        <div
          key={day}
          onClick={() => handleDateClick(day)}
          className={`h-24 p-2 border cursor-pointer transition-all duration-200 hover:bg-gray-50 ${
            isToday 
              ? 'bg-blue-50 border-blue-200' 
              : dayHolidays.length > 0
              ? 'bg-green-50 border-green-200'
              : theme === 'dark' 
              ? 'bg-gray-800 border-gray-700 hover:bg-gray-700' 
              : 'bg-white border-gray-200'
          }`}
        >
          <div className={`text-sm font-medium mb-1 ${isToday ? 'text-blue-600' : theme === 'dark' ? 'text-white' : 'text-gray-900'}`}>
            {day}
          </div>
          <div className="space-y-1">
            {dayHolidays.slice(0, 2).map((holiday) => (
              <div
                key={holiday._id}
                className="group flex items-center justify-between bg-green-100 text-green-800 px-2 py-1 rounded text-xs"
              >
                <span className="truncate flex-1">{holiday.name}</span>
                <div className="flex gap-1 opacity-0 group-hover:opacity-100 transition-opacity">
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      handleEditHoliday(holiday);
                    }}
                    className="w-4 h-4 bg-blue-500 hover:bg-blue-600 rounded text-white text-xs flex items-center justify-center"
                  >
                    ✎
                  </button>
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      handleDeleteHoliday(holiday._id);
                    }}
                    className="w-4 h-4 bg-red-500 hover:bg-red-600 rounded text-white text-xs flex items-center justify-center"
                  >
                    ×
                  </button>
                </div>
              </div>
            ))}
            {dayHolidays.length > 2 && (
              <div className="text-xs text-gray-500">+{dayHolidays.length - 2} more</div>
            )}
          </div>
        </div>
      );
    }

    return days;
  };

  const upcomingHolidays = holidays
    .filter(h => new Date(h.date) >= new Date())
    .sort((a, b) => new Date(a.date) - new Date(b.date))
    .slice(0, 3);

  return (
    <div className={`${theme === 'dark' ? 'bg-gray-900 text-white' : 'bg-gray-50'} min-h-screen p-6`}>
      <div className="max-w-6xl mx-auto">
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">

          {/* Main Calendar */}
          <div className="lg:col-span-3">
            <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow-sm border ${theme === 'dark' ? 'border-gray-700' : 'border-gray-200'}`}>

              {/* Header */}
              <div className="p-6 border-b border-gray-200">
                <div className="flex items-center justify-between">
                  <button
                    onClick={() => navigateMonth(-1)}
                    className={`p-2 rounded-lg ${theme === 'dark' ? 'hover:bg-gray-700' : 'hover:bg-gray-100'} transition-colors`}
                  >
                    <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M12.707 5.293a1 1 0 010 1.414L9.414 10l3.293 3.293a1 1 0 01-1.414 1.414l-4-4a1 1 0 010-1.414l4-4a1 1 0 011.414 0z" clipRule="evenodd" />
                    </svg>
                  </button>
                  <h2 className="text-xl font-semibold">
                    {months[currentDate.getMonth()]} {currentDate.getFullYear()}
                  </h2>
                  <button
                    onClick={() => navigateMonth(1)}
                    className={`p-2 rounded-lg ${theme === 'dark' ? 'hover:bg-gray-700' : 'hover:bg-gray-100'} transition-colors`}
                  >
                    <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M7.293 14.707a1 1 0 010-1.414L10.586 10 7.293 6.707a1 1 0 011.414-1.414l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414 0z" clipRule="evenodd" />
                    </svg>
                  </button>
                </div>
              </div>

              {/* Days Header */}
              <div className="grid grid-cols-7 border-b border-gray-200">
                {days.map(day => (
                  <div key={day} className="p-3 text-center text-sm font-medium text-gray-500 border-r border-gray-200 last:border-r-0">
                    {day}
                  </div>
                ))}
              </div>

              {/* Calendar Grid */}
              <div className="grid grid-cols-7">
                {renderCalendarDays()}
              </div>
            </div>
          </div>

          {/* Sidebar */}
          <div className="space-y-6">

            {/* Add Holiday */}
            <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow-sm border ${theme === 'dark' ? 'border-gray-700' : 'border-gray-200'} p-4`}>
              <h3 className="font-semibold mb-3">Quick Add Holiday</h3>
              <div className="space-y-3">
                <input
                  type="text"
                  placeholder="Holiday name"
                  className={`w-full p-2 border rounded ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'} text-sm`}
                  onKeyPress={(e) => {
                    if (e.key === 'Enter' && e.target.value.trim()) {
                      const today = new Date().toISOString().split('T')[0];
                      onAddHoliday({
                        name: e.target.value,
                        date: today,
                        year: new Date().getFullYear()
                      });
                      e.target.value = '';
                    }
                  }}
                />
                <p className="text-xs text-gray-500">Press Enter to add for today</p>
              </div>
            </div>

            {/* Upcoming Holidays */}
            <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow-sm border ${theme === 'dark' ? 'border-gray-700' : 'border-gray-200'} p-4`}>
              <h3 className="font-semibold mb-3">Upcoming Holidays</h3>
              <div className="space-y-2">
                {upcomingHolidays.map((holiday) => (
                  <div key={holiday._id} className="flex justify-between items-center p-2 bg-green-50 rounded text-sm">
                    <div>
                      <div className="font-medium text-green-800">{holiday.name}</div>
                      <div className="text-green-600 text-xs">
                        {new Date(holiday.date).toLocaleDateString()}
                      </div>
                    </div>
                  </div>
                ))}
                {upcomingHolidays.length === 0 && (
                  <p className="text-gray-500 text-sm">No upcoming holidays</p>
                )}
              </div>
            </div>

            {/* Stats */}
            <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow-sm border ${theme === 'dark' ? 'border-gray-700' : 'border-gray-200'} p-4`}>
              <h3 className="font-semibold mb-3">Statistics</h3>
              <div className="space-y-2 text-sm">
                <div className="flex justify-between">
                  <span>Total Holidays</span>
                  <span className="font-medium">{holidays.length}</span>
                </div>
                <div className="flex justify-between">
                  <span>This Month</span>
                  <span className="font-medium">
                    {holidays.filter(h => {
                      const hDate = new Date(h.date);
                      return hDate.getMonth() === currentDate.getMonth() && hDate.getFullYear() === currentDate.getFullYear();
                    }).length}
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow-lg max-w-md w-full mx-4 p-6`}>
            <h3 className="text-lg font-semibold mb-4">
              {editingHoliday ? 'Edit Holiday' : 'Add Holiday'}
            </h3>

            {/* Existing Holidays */}
            {selectedDate && (
              <div className="mb-4">
                <p className="text-sm text-gray-600 mb-2">{selectedDate.toLocaleDateString()}</p>
                {getHolidaysForDate(selectedDate.getDate()).map((holiday) => (
                  <div key={holiday._id} className="flex items-center justify-between p-2 bg-green-50 rounded mb-2">
                    <span className="text-green-800 text-sm">{holiday.name}</span>
                    <div className="flex gap-1">
                      <button
                        onClick={() => handleEditHoliday(holiday)}
                        className="px-2 py-1 bg-blue-500 text-white rounded text-xs hover:bg-blue-600"
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => handleDeleteHoliday(holiday._id)}
                        className="px-2 py-1 bg-red-500 text-white rounded text-xs hover:bg-red-600"
                      >
                        Delete
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}

            {/* Form */}
            <div className="space-y-4">
              <input
                type="text"
                placeholder="Holiday name"
                value={holidayForm.name}
                onChange={(e) => setHolidayForm({...holidayForm, name: e.target.value})}
                className={`w-full p-3 border rounded ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
              />
              <input
                type="date"
                value={holidayForm.date}
                onChange={(e) => setHolidayForm({...holidayForm, date: e.target.value})}
                className={`w-full p-3 border rounded ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
              />
              <div className="flex gap-3">
                <button
                  onClick={handleSaveHoliday}
                  className="flex-1 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors"
                >
                  {editingHoliday ? 'Update' : 'Add'}
                </button>
                <button
                  onClick={() => {
                    setShowModal(false);
                    setEditingHoliday(null);
                    setHolidayForm({ name: '', date: '' });
                  }}
                  className={`flex-1 py-2 rounded transition-colors ${
                    theme === 'dark' ? 'bg-gray-600 hover:bg-gray-500' : 'bg-gray-200 hover:bg-gray-300'
                  }`}
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProfessionalCalendar;
