import React, { useState, useEffect } from 'react';
import axios from 'axios';

function EmployeeProfile() {
  const [profile, setProfile] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    employeeId: '',
    dateOfBirth: '',
    dateOfJoining: '',
    department: '',
    position: ''
  });
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchProfile();
  }, []);

  const fetchProfile = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/employee/profile', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setProfile(response.data);
      setFormData({
        employeeId: response.data.employeeId,
        dateOfBirth: response.data.dateOfBirth?.split('T')[0] || '',
        dateOfJoining: response.data.dateOfJoining?.split('T')[0] || '',
        department: response.data.department || '',
        position: response.data.position || ''
      });
    } catch (error) {
      if (error.response?.status === 404) {
        setIsEditing(true); // Show form to create profile
      }
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const url = profile 
        ? 'http://localhost:5000/api/employee/profile'
        : 'http://localhost:5000/api/employee/profile';

      const method = profile ? 'put' : 'post';

      const response = await axios[method](url, formData, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setProfile(response.data.employee);
      setIsEditing(false);
      setMessage(response.data.message);
    } catch (error) {
      setMessage(error.response?.data?.message || 'Operation failed');
    }
  };

  if (isEditing || !profile) {
    return (
      <div className="employee-profile">
        <h2>{profile ? 'Edit Profile' : 'Create Profile'}</h2>
        <form onSubmit={handleSubmit} className="profile-form">
          <div className="form-group">
            <label>Employee ID:</label>
            <input
              type="text"
              name="employeeId"
              value={formData.employeeId}
              onChange={handleChange}
              required
              disabled={profile} // Can't change ID after creation
            />
          </div>

          <div className="form-group">
            <label>Date of Birth:</label>
            <input
              type="date"
              name="dateOfBirth"
              value={formData.dateOfBirth}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Date of Joining:</label>
            <input
              type="date"
              name="dateOfJoining"
              value={formData.dateOfJoining}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Department:</label>
            <input
              type="text"
              name="department"
              value={formData.department}
              onChange={handleChange}
              placeholder="e.g., IT, HR, Finance"
            />
          </div>

          <div className="form-group">
            <label>Position:</label>
            <input
              type="text"
              name="position"
              value={formData.position}
              onChange={handleChange}
              placeholder="e.g., Software Developer, Manager"
            />
          </div>

          <div className="form-actions">
            <button type="submit" className="btn-primary">
              {profile ? 'Update Profile' : 'Create Profile'}
            </button>
            {profile && (
              <button type="button" onClick={() => setIsEditing(false)} className="btn-secondary">
                Cancel
              </button>
            )}
          </div>
        </form>
        {message && <div className="message">{message}</div>}
      </div>
    );
  }

  return (
    <div className="employee-profile">
      <div className="profile-header">
        <h2>My Profile</h2>
        <button onClick={() => setIsEditing(true)} className="btn-primary">
          Edit Profile
        </button>
      </div>

      <div className="profile-details">
        <div className="detail-item">
          <label>Employee ID:</label>
          <span>{profile.employeeId}</span>
        </div>

        <div className="detail-item">
          <label>Name:</label>
          <span>{profile.name}</span>
        </div>

        <div className="detail-item">
          <label>Email:</label>
          <span>{profile.email}</span>
        </div>

        <div className="detail-item">
          <label>Date of Birth:</label>
          <span>{new Date(profile.dateOfBirth).toLocaleDateString()}</span>
        </div>

        <div className="detail-item">
          <label>Date of Joining:</label>
          <span>{new Date(profile.dateOfJoining).toLocaleDateString()}</span>
        </div>

        <div className="detail-item">
          <label>Department:</label>
          <span>{profile.department || 'Not specified'}</span>
        </div>

        <div className="detail-item">
          <label>Position:</label>
          <span>{profile.position || 'Not specified'}</span>
        </div>

        <div className="detail-item">
          <label>Available Leaves:</label>
          <span>{profile.availableLeaves} days</span>
        </div>
      </div>

      {message && <div className="message">{message}</div>}
    </div>
  );
}

export default EmployeeProfile;
