import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AuthPage() {
  const [isSignUp, setIsSignUp] = useState(false);
  const [showForgotPassword, setShowForgotPassword] = useState(false);
  const [formData, setFormData] = useState({ 
    name: '', email: '', password: '', role: 'employee',
    employeeId: '', dateOfBirth: '', dateOfJoining: '', availableLeaves: '20'
  });
  const [forgotEmail, setForgotEmail] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const token = urlParams.get('token');
    const user = urlParams.get('user');

    if (token && user) {
      localStorage.setItem('token', token);
      localStorage.setItem('user', user);
      setMessage('Google login successful!');
      window.history.replaceState({}, document.title, '/auth');
    }
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const endpoint = isSignUp ? 'http://localhost:5000/api/register' : 'http://localhost:5000/api/login';
      const data = isSignUp ? formData : { email: formData.email, password: formData.password };

      const response = await axios.post(endpoint, data);

      if (isSignUp && formData.role === 'admin') {
        localStorage.setItem('token', response.data.token);
        localStorage.setItem('user', JSON.stringify(response.data.user));
        localStorage.setItem('adminCredentials', JSON.stringify(response.data.workCredentials));
        setMessage(
          <div>
            <strong>Admin Registration Successful!</strong><br/>
            Work Email: {response.data.workCredentials.workEmail}<br/>
            Work Password: {response.data.workCredentials.workPassword}<br/>
            <small>Redirecting to dashboard...</small>
          </div>
        );
        setTimeout(() => {
          window.location.href = '/dashboard';
        }, 2000);
      } else {
        localStorage.setItem('token', response.data.token);
        localStorage.setItem('user', JSON.stringify(response.data.user));
        setMessage(`${isSignUp ? 'Registration' : 'Login'} successful!`);

        setTimeout(() => {
          window.location.href = '/dashboard';
        }, 1000);
      }
    } catch (error) {
      const errorData = error.response?.data;
      setMessage(errorData?.message || 'Operation failed');
    }
  };

  const handleForgotPassword = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/forgot-password', { email: forgotEmail });
      setMessage(response.data.message);
      setShowForgotPassword(false);
    } catch (error) {
      setMessage(error.response?.data?.message || 'Failed to send reset email');
    }
  };

  const handleGoogleLogin = (e) => {
    e.preventDefault();
    e.stopPropagation();
    window.location.href = 'http://localhost:5000/api/auth/google';
  };

  const toggleMode = () => {
    setIsSignUp(!isSignUp);
    setFormData({ 
      name: '', email: '', password: '', role: 'employee',
      employeeId: '', dateOfBirth: '', dateOfJoining: '', availableLeaves: '20'
    });
    setMessage('');
    setShowForgotPassword(false);
  };

  const showForgotForm = (e) => {
    e.preventDefault();
    setShowForgotPassword(true);
    setMessage('');
  };

  const hideForgotForm = () => {
    setShowForgotPassword(false);
    setForgotEmail('');
    setMessage('');
  };

  if (showForgotPassword) {
    return (
      <div className="min-h-screen bg-gray-50 flex flex-col justify-center py-12 sm:px-6 lg:px-8">
        <div className="sm:mx-auto sm:w-full sm:max-w-md">
          <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">Forgot Password</h2>
          <p className="mt-2 text-center text-sm text-gray-600">
            Enter your email to receive a password reset link
          </p>
        </div>

        <div className="mt-8 sm:mx-auto sm:w-full sm:max-w-md">
          <div className="bg-white py-8 px-4 shadow sm:rounded-lg sm:px-10">
            <form onSubmit={handleForgotPassword}>
              <div>
                <input
                  type="email"
                  placeholder="Enter your email"
                  value={forgotEmail}
                  onChange={(e) => setForgotEmail(e.target.value)}
                  className="appearance-none block w-full px-3 py-2 border border-gray-300 rounded-md placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                  required
                />
              </div>

              <div className="mt-6">
                <button type="submit" className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500">
                  Send Reset Link
                </button>
              </div>

              <div className="mt-4">
                <button type="button" onClick={hideForgotForm} className="w-full flex justify-center py-2 px-4 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500">
                  Back to Login
                </button>
              </div>
            </form>
          </div>
        </div>
        {message && <div className="mt-4 text-center text-green-600">{message}</div>}
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col justify-center py-12 sm:px-6 lg:px-8">
      <div className="sm:mx-auto sm:w-full sm:max-w-md">
        <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
          {isSignUp ? 'Create Account' : 'Sign In'}
        </h2>
      </div>

      <div className="mt-8 sm:mx-auto sm:w-full sm:max-w-md">
        <div className="bg-white py-8 px-4 shadow sm:rounded-lg sm:px-10">
          <div className="mb-6">
            <div className="flex justify-center space-x-4">
              <button
                type="button"
                className={`px-4 py-2 rounded-md text-sm font-medium ${formData.role === 'employee' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
                onClick={() => setFormData({...formData, role: 'employee'})}
              >
                USER
              </button>
              <span className="text-gray-500">OR</span>
              <button
                type="button"
                className={`px-4 py-2 rounded-md text-sm font-medium ${formData.role === 'admin' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
                onClick={() => setFormData({...formData, role: 'admin'})}
              >
                ADMIN
              </button>
            </div>
          </div>

          <form onSubmit={handleSubmit} className="space-y-6">
            {isSignUp && (
              <div>
                <input
                  type="text"
                  name="name"
                  placeholder="Full Name"
                  value={formData.name}
                  onChange={handleChange}
                  className="appearance-none block w-full px-3 py-2 border border-gray-300 rounded-md placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                  required
                />
              </div>
            )}

            <div>
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
                className="appearance-none block w-full px-3 py-2 border border-gray-300 rounded-md placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                required
              />
            </div>

            <div>
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleChange}
                className="appearance-none block w-full px-3 py-2 border border-gray-300 rounded-md placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                required
              />
            </div>

            {isSignUp && formData.role === 'employee' && (
              <>
                <div>
                  <input
                    type="text"
                    name="employeeId"
                    placeholder="Employee ID"
                    value={formData.employeeId}
                    onChange={handleChange}
                    className="appearance-none block w-full px-3 py-2 border border-gray-300 rounded-md placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                    required
                  />
                </div>
                <div>
                  <input
                    type="date"
                    name="dateOfBirth"
                    value={formData.dateOfBirth}
                    onChange={handleChange}
                    className="appearance-none block w-full px-3 py-2 border border-gray-300 rounded-md placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                    required
                  />
                </div>
                <div>
                  <input
                    type="date"
                    name="dateOfJoining"
                    value={formData.dateOfJoining}
                    onChange={handleChange}
                    className="appearance-none block w-full px-3 py-2 border border-gray-300 rounded-md placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                    required
                  />
                </div>
              </>
            )}

            <div>
              <button type="submit" className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500">
                {isSignUp ? 'Sign Up' : 'Sign In'}
              </button>
            </div>

            <div>
              <button type="button" onClick={handleGoogleLogin} className="w-full flex justify-center items-center py-2 px-4 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500">
                <span className="mr-2">G</span>
                Continue with Google
              </button>
            </div>

            {!isSignUp && (
              <div className="text-center">
                <a href="#" onClick={showForgotForm} className="text-sm text-blue-600 hover:text-blue-500">
                  Forgot Password?
                </a>
              </div>
            )}
          </form>

          <div className="mt-6 text-center">
            <button onClick={toggleMode} className="text-sm text-blue-600 hover:text-blue-500">
              {isSignUp ? 'Already have an account? Sign In' : "Don't have an account? Sign Up"}
            </button>
          </div>
        </div>
      </div>

      {message && <div className="mt-4 text-center text-green-600">{message}</div>}
    </div>
  );
}

export default AuthPage;
