import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminPanel.css';

function AdminPanel() {
  const [activeTab, setActiveTab] = useState('employees');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    dateOfBirth: '',
    dateOfJoining: '',
    availableLeaves: '',
    department: '',
    position: ''
  });
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/admin/users', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUsers(response.data);
    } catch (error) {
      setMessage(error.response?.data?.message || 'Failed to fetch users');
    }
  };



  const handleEdit = (user) => {
    if (!user.hasProfile) {
      setMessage('User has not created their profile yet');
      return;
    }
    setSelectedUser(user);
    setFormData({
      name: user.profile.name,
      email: user.profile.email,
      dateOfBirth: user.profile.dateOfBirth?.split('T')[0] || '',
      dateOfJoining: user.profile.dateOfJoining?.split('T')[0] || '',
      availableLeaves: user.profile.availableLeaves,
      department: user.profile.department || '',
      position: user.profile.position || ''
    });
    setIsEditing(true);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.put(`http://localhost:5000/api/admin/employees/${selectedUser.profile._id}`, formData, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setMessage('Employee updated successfully');
      setIsEditing(false);
      setSelectedUser(null);
      fetchUsers();
    } catch (error) {
      setMessage(error.response?.data?.message || 'Update failed');
    }
  };

  const handleDelete = async (user) => {
    if (!user.hasProfile) {
      setMessage('Cannot delete user without profile');
      return;
    }
    if (window.confirm('Are you sure you want to delete this employee profile?')) {
      try {
        const token = localStorage.getItem('token');
        await axios.delete(`http://localhost:5000/api/admin/employees/${user.profile._id}`, {
          headers: { Authorization: `Bearer ${token}` }
        });

        setMessage('Employee profile deleted successfully');
        fetchUsers();
      } catch (error) {
        setMessage(error.response?.data?.message || 'Delete failed');
      }
    }
  };

  if (isEditing) {
    return (
      <div className="admin-panel">
        <h2>Edit Employee Profile</h2>
        <form onSubmit={handleSubmit} className="employee-form">
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Date of Birth:</label>
            <input
              type="date"
              name="dateOfBirth"
              value={formData.dateOfBirth}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Date of Joining:</label>
            <input
              type="date"
              name="dateOfJoining"
              value={formData.dateOfJoining}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Available Leaves:</label>
            <input
              type="number"
              name="availableLeaves"
              value={formData.availableLeaves}
              onChange={handleChange}
              min="0"
              required
            />
          </div>

          <div className="form-group">
            <label>Department:</label>
            <input
              type="text"
              name="department"
              value={formData.department}
              onChange={handleChange}
            />
          </div>

          <div className="form-group">
            <label>Position:</label>
            <input
              type="text"
              name="position"
              value={formData.position}
              onChange={handleChange}
            />
          </div>

          <div className="form-actions">
            <button type="submit" className="btn-primary">Update Employee</button>
            <button type="button" onClick={() => setIsEditing(false)} className="btn-secondary">
              Cancel
            </button>
          </div>
        </form>
        {message && <div className="message">{message}</div>}
      </div>
    );
  }

  const currentUser = JSON.parse(localStorage.getItem('user') || '{}');

  return (
    <div className="admin-container">
      {/* Sidebar */}
      <div className={`admin-sidebar ${sidebarOpen ? 'open' : ''}`}>
        <div className="sidebar-header">
          <h3 className="sidebar-title">Admin Panel</h3>
        </div>
        <nav className="sidebar-nav">
          <button 
            className={`nav-button ${activeTab === 'dashboard' ? 'active' : ''}`}
            onClick={() => setActiveTab('dashboard')}
          >
            Dashboard
          </button>
          <button 
            className={`nav-button ${activeTab === 'employees' ? 'active' : ''}`}
            onClick={() => setActiveTab('employees')}
          >
            Employee Management
          </button>
          <button 
            className={`nav-button ${activeTab === 'settings' ? 'active' : ''}`}
            onClick={() => setActiveTab('settings')}
          >
            Settings
          </button>
          <button 
            className="nav-button"
            onClick={() => {
              localStorage.clear();
              window.location.href = '/';
            }}
          >
            Logout
          </button>
        </nav>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* Header */}
        <div className="main-header">
          <div className="header-content">
            <div className="header-flex">
              <div className="header-left">
                <button
                  className="menu-button"
                  onClick={() => setSidebarOpen(!sidebarOpen)}
                >
                  <svg width="24" height="24" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                  </svg>
                </button>
              </div>
              <div className="header-right">
                <span className="user-name">Welcome, {currentUser.name || 'Admin'}</span>
                <span className="admin-badge">
                  ADMIN
                </span>
                <button 
                  className="logout-button"
                  onClick={() => {
                    localStorage.clear();
                    window.location.href = '/';
                  }}
                >
                  Logout
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Content Area */}
        <div className="content-area">
          {activeTab === 'employees' && (
            <div>
              <h2 className="page-title">Employee Management</h2>
              <div className="table-container">
                <table className="data-table">
                  <thead className="table-header">
                    <tr>
                      <th>Name</th>
                      <th>Email</th>
                      <th>Status</th>
                      <th>Employee ID</th>
                      <th>Department</th>
                      <th>Joined</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody className="table-body">
                    {users.filter(user => user.role === 'employee').map((user, index) => (
                      <tr key={user._id} className="table-row">
                        <td className="table-cell cell-name">{user.name}</td>
                        <td className="table-cell cell-text">{user.email}</td>
                        <td className="table-cell">
                          <span className={`status-badge ${
                            user.hasProfile ? 'status-complete' : 'status-pending'
                          }`}>
                            {user.hasProfile ? 'Profile Complete' : 'Profile Pending'}
                          </span>
                        </td>
                        <td className="table-cell cell-text">{user.profile?.employeeId || 'N/A'}</td>
                        <td className="table-cell cell-text">{user.profile?.department || 'N/A'}</td>
                        <td className="table-cell cell-text">{new Date(user.createdAt).toLocaleDateString()}</td>
                        <td className="table-cell">
                          {user.hasProfile ? (
                            <div className="action-buttons">
                              <button 
                                onClick={() => handleEdit(user)}
                                className="btn-edit"
                              >
                                Edit
                              </button>
                              <button 
                                onClick={() => handleDelete(user)}
                                className="btn-delete"
                              >
                                Delete
                              </button>
                            </div>
                          ) : (
                            <span className="waiting-text">Waiting for profile</span>
                          )}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>

                {users.filter(user => user.role === 'employee').length === 0 && (
                  <div className="empty-state">
                    <p className="empty-text">No employees found.</p>
                  </div>
                )}
              </div>
            </div>
          )}

          {activeTab === 'dashboard' && (
            <div>
              <h2 className="page-title">Dashboard Overview</h2>
              <div className="dashboard-grid">
                <div className="stat-card">
                  <div className="stat-content">
                    <div className="stat-number blue">{users.filter(u => u.role === 'employee').length}</div>
                    <p className="stat-label">Total Employees</p>
                  </div>
                </div>
                <div className="stat-card">
                  <div className="stat-content">
                    <div className="stat-number green">{users.filter(u => u.hasProfile).length}</div>
                    <p className="stat-label">Complete Profiles</p>
                  </div>
                </div>
                <div className="stat-card">
                  <div className="stat-content">
                    <div className="stat-number yellow">{users.filter(u => !u.hasProfile && u.role === 'employee').length}</div>
                    <p className="stat-label">Pending Profiles</p>
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'settings' && (
            <div>
              <h2 className="page-title">Settings</h2>
              <div className="settings-panel">
                <p className="empty-text">Settings panel coming soon...</p>
              </div>
            </div>
          )}
        </div>
      </div>

      {message && (
        <div className="message-toast">
          {message}
        </div>
      )}
    </div>
  );
}

export default AdminPanel;
