import React, { useState, useEffect } from 'react';

function Payroll({ theme }) {
  const [employees, setEmployees] = useState([]);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [payrollData, setPayrollData] = useState({
    basicSalary: 0,
    hra: 0,
    da: 0,
    bonus: 0,
    overtime: 0,
    leaveDeductions: 0,
    taxDeduction: 0,
    netSalary: 0
  });
  const [showPayslip, setShowPayslip] = useState(false);
  const [currentMonth, setCurrentMonth] = useState(new Date().getMonth());
  const [currentYear, setCurrentYear] = useState(new Date().getFullYear());

  useEffect(() => {
    fetchEmployees();
  }, []);

  useEffect(() => {
    if (selectedEmployee) {
      const calculated = calculatePayroll(selectedEmployee);
      setPayrollData(calculated);
    }
  }, [selectedEmployee]);

  const fetchEmployees = async () => {
    try {
      const response = await fetch('http://localhost:5002/api/employees', {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
      });
      const data = await response.json();
      setEmployees(data);
    } catch (error) {
      console.error('Error fetching employees:', error);
    }
  };

  const calculatePayroll = (employee) => {
    const basicSalary = employee.salary || 50000;
    const hra = basicSalary * 0.4; // 40% HRA
    const da = basicSalary * 0.12; // 12% DA
    const grossSalary = basicSalary + hra + da;

    // Leave deductions (₹500 per unpaid leave)
    const unpaidLeaves = Math.max(0, (employee.totalLeavesTaken || 0) - (employee.availableLeaves || 20));
    const leaveDeductions = unpaidLeaves * 500;

    // Tax calculation (simplified)
    let taxDeduction = 0;
    const annualSalary = grossSalary * 12;
    if (annualSalary > 1000000) taxDeduction = grossSalary * 0.3;
    else if (annualSalary > 500000) taxDeduction = grossSalary * 0.2;
    else if (annualSalary > 250000) taxDeduction = grossSalary * 0.05;

    const netSalary = grossSalary + (payrollData.bonus || 0) + (payrollData.overtime || 0) - leaveDeductions - taxDeduction;

    return {
      basicSalary,
      hra,
      da,
      grossSalary,
      bonus: payrollData.bonus || 0,
      overtime: payrollData.overtime || 0,
      leaveDeductions,
      taxDeduction,
      netSalary
    };
  };

  const handleEmployeeSelect = (employee) => {
    setSelectedEmployee(employee);
    const calculated = calculatePayroll(employee);
    setPayrollData(calculated);
  };

  const generatePayslip = () => {
    if (!selectedEmployee) return;

    const payslipData = calculatePayroll(selectedEmployee);
    const monthNames = ["January", "February", "March", "April", "May", "June",
      "July", "August", "September", "October", "November", "December"];

    const payslipContent = `
      <div style="font-family: Arial, sans-serif; max-width: 800px; margin: 0 auto; padding: 20px;">
        <div style="text-align: center; border-bottom: 2px solid #333; padding-bottom: 20px; margin-bottom: 20px;">
          <h1 style="color: #333; margin: 0;">CzarCore Technologies</h1>
          <h2 style="color: #666; margin: 10px 0;">SALARY SLIP</h2>
          <p style="margin: 5px 0;">For the month of ${monthNames[currentMonth]} ${currentYear}</p>
        </div>

        <div style="display: flex; justify-content: space-between; margin-bottom: 30px;">
          <div>
            <h3 style="color: #333; border-bottom: 1px solid #ccc; padding-bottom: 5px;">Employee Details</h3>
            <p><strong>Name:</strong> ${selectedEmployee.name}</p>
            <p><strong>Employee ID:</strong> ${selectedEmployee.employeeId}</p>
            <p><strong>Department:</strong> ${selectedEmployee.department}</p>
            <p><strong>Position:</strong> ${selectedEmployee.position}</p>
          </div>
          <div>
            <h3 style="color: #333; border-bottom: 1px solid #ccc; padding-bottom: 5px;">Pay Period</h3>
            <p><strong>Month:</strong> ${monthNames[currentMonth]} ${currentYear}</p>
            <p><strong>Working Days:</strong> 22</p>
            <p><strong>Paid Days:</strong> ${22 - (selectedEmployee.totalLeavesTaken || 0)}</p>
          </div>
        </div>

        <table style="width: 100%; border-collapse: collapse; margin-bottom: 20px;">
          <tr style="background-color: #f5f5f5;">
            <th style="border: 1px solid #ddd; padding: 12px; text-align: left;">EARNINGS</th>
            <th style="border: 1px solid #ddd; padding: 12px; text-align: right;">AMOUNT (₹)</th>
            <th style="border: 1px solid #ddd; padding: 12px; text-align: left;">DEDUCTIONS</th>
            <th style="border: 1px solid #ddd; padding: 12px; text-align: right;">AMOUNT (₹)</th>
          </tr>
          <tr>
            <td style="border: 1px solid #ddd; padding: 8px;">Basic Salary</td>
            <td style="border: 1px solid #ddd; padding: 8px; text-align: right;">₹${payslipData.basicSalary.toLocaleString()}</td>
            <td style="border: 1px solid #ddd; padding: 8px;">Income Tax</td>
            <td style="border: 1px solid #ddd; padding: 8px; text-align: right;">₹${payslipData.taxDeduction.toLocaleString()}</td>
          </tr>
          <tr>
            <td style="border: 1px solid #ddd; padding: 8px;">HRA (40%)</td>
            <td style="border: 1px solid #ddd; padding: 8px; text-align: right;">₹${payslipData.hra.toLocaleString()}</td>
            <td style="border: 1px solid #ddd; padding: 8px;">Leave Deductions</td>
            <td style="border: 1px solid #ddd; padding: 8px; text-align: right;">₹${payslipData.leaveDeductions.toLocaleString()}</td>
          </tr>
          <tr>
            <td style="border: 1px solid #ddd; padding: 8px;">DA (12%)</td>
            <td style="border: 1px solid #ddd; padding: 8px; text-align: right;">₹${payslipData.da.toLocaleString()}</td>
            <td style="border: 1px solid #ddd; padding: 8px;">PF (12%)</td>
            <td style="border: 1px solid #ddd; padding: 8px; text-align: right;">₹${(payslipData.basicSalary * 0.12).toLocaleString()}</td>
          </tr>
          <tr>
            <td style="border: 1px solid #ddd; padding: 8px;">Bonus</td>
            <td style="border: 1px solid #ddd; padding: 8px; text-align: right;">₹${payslipData.bonus.toLocaleString()}</td>
            <td style="border: 1px solid #ddd; padding: 8px;">ESI (1.75%)</td>
            <td style="border: 1px solid #ddd; padding: 8px; text-align: right;">₹${(payslipData.grossSalary * 0.0175).toLocaleString()}</td>
          </tr>
          <tr>
            <td style="border: 1px solid #ddd; padding: 8px;">Overtime</td>
            <td style="border: 1px solid #ddd; padding: 8px; text-align: right;">₹${payslipData.overtime.toLocaleString()}</td>
            <td style="border: 1px solid #ddd; padding: 8px;"></td>
            <td style="border: 1px solid #ddd; padding: 8px; text-align: right;"></td>
          </tr>
          <tr style="background-color: #f9f9f9; font-weight: bold;">
            <td style="border: 1px solid #ddd; padding: 12px;">GROSS EARNINGS</td>
            <td style="border: 1px solid #ddd; padding: 12px; text-align: right;">₹${(payslipData.grossSalary + payslipData.bonus + payslipData.overtime).toLocaleString()}</td>
            <td style="border: 1px solid #ddd; padding: 12px;">TOTAL DEDUCTIONS</td>
            <td style="border: 1px solid #ddd; padding: 12px; text-align: right;">₹${(payslipData.taxDeduction + payslipData.leaveDeductions + payslipData.basicSalary * 0.12 + payslipData.grossSalary * 0.0175).toLocaleString()}</td>
          </tr>
        </table>

        <div style="background-color: #e8f5e8; padding: 15px; border-radius: 5px; text-align: center;">
          <h2 style="color: #2d5a2d; margin: 0;">NET SALARY: ₹${(payslipData.netSalary - payslipData.basicSalary * 0.12 - payslipData.grossSalary * 0.0175).toLocaleString()}</h2>
        </div>

        <div style="margin-top: 30px; text-align: center; font-size: 12px; color: #666;">
          <p>This is a computer-generated payslip and does not require a signature.</p>
          <p>Generated on: ${new Date().toLocaleDateString()}</p>
        </div>
      </div>
    `;

    const newWindow = window.open('', '_blank');
    newWindow.document.write(payslipContent);
    newWindow.document.close();
    newWindow.print();
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold">Payroll Management</h2>
        <div className="flex gap-2">
          <select
            value={currentMonth}
            onChange={(e) => setCurrentMonth(parseInt(e.target.value))}
            className={`px-3 py-2 rounded-lg border ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
          >
            {Array.from({length: 12}, (_, i) => (
              <option key={i} value={i}>{new Date(0, i).toLocaleString('default', {month: 'long'})}</option>
            ))}
          </select>
          <select
            value={currentYear}
            onChange={(e) => setCurrentYear(parseInt(e.target.value))}
            className={`px-3 py-2 rounded-lg border ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
          >
            {Array.from({length: 5}, (_, i) => (
              <option key={i} value={new Date().getFullYear() - 2 + i}>{new Date().getFullYear() - 2 + i}</option>
            ))}
          </select>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Employee Selection */}
        <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-lg shadow-md`}>
          <h3 className="text-lg font-semibold mb-4">Select Employee</h3>
          <div className="space-y-2 max-h-96 overflow-y-auto">
            {employees.map(employee => (
              <div
                key={employee._id}
                onClick={() => handleEmployeeSelect(employee)}
                className={`p-3 rounded-lg cursor-pointer transition-colors ${
                  selectedEmployee?._id === employee._id
                    ? 'bg-blue-100 border-blue-300'
                    : theme === 'dark' ? 'bg-gray-700 hover:bg-gray-600' : 'bg-gray-50 hover:bg-gray-100'
                }`}
              >
                <div className="flex justify-between items-center">
                  <div>
                    <p className="font-medium">{employee.name}</p>
                    <p className="text-sm text-gray-500">{employee.employeeId} - {employee.department}</p>
                  </div>
                  <p className="text-sm font-medium">₹{(employee.salary || 50000).toLocaleString()}</p>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Payroll Calculation */}
        {selectedEmployee && (
          <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-lg shadow-md`}>
            <h3 className="text-lg font-semibold mb-4">Payroll Details</h3>

            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-1">Bonus (₹)</label>
                  <input
                    type="number"
                    value={payrollData.bonus}
                    onChange={(e) => setPayrollData({...payrollData, bonus: parseFloat(e.target.value) || 0})}
                    className={`w-full px-3 py-2 rounded-lg border ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium mb-1">Overtime (₹)</label>
                  <input
                    type="number"
                    value={payrollData.overtime}
                    onChange={(e) => setPayrollData({...payrollData, overtime: parseFloat(e.target.value) || 0})}
                    className={`w-full px-3 py-2 rounded-lg border ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                  />
                </div>
              </div>

              <div className="border-t pt-4">
                <h4 className="font-medium mb-3">Salary Breakdown</h4>
                <div className="space-y-2 text-sm">
                  <div className="flex justify-between">
                    <span>Basic Salary:</span>
                    <span>₹{payrollData.basicSalary.toLocaleString()}</span>
                  </div>
                  <div className="flex justify-between">
                    <span>HRA (40%):</span>
                    <span>₹{payrollData.hra.toLocaleString()}</span>
                  </div>
                  <div className="flex justify-between">
                    <span>DA (12%):</span>
                    <span>₹{payrollData.da.toLocaleString()}</span>
                  </div>
                  <div className="flex justify-between">
                    <span>Bonus:</span>
                    <span>₹{payrollData.bonus.toLocaleString()}</span>
                  </div>
                  <div className="flex justify-between">
                    <span>Overtime:</span>
                    <span>₹{payrollData.overtime.toLocaleString()}</span>
                  </div>
                  <div className="flex justify-between text-red-600">
                    <span>Leave Deductions:</span>
                    <span>-₹{payrollData.leaveDeductions.toLocaleString()}</span>
                  </div>
                  <div className="flex justify-between text-red-600">
                    <span>Tax Deduction:</span>
                    <span>-₹{payrollData.taxDeduction.toLocaleString()}</span>
                  </div>
                  <div className="flex justify-between text-red-600">
                    <span>PF (12%):</span>
                    <span>-₹{(payrollData.basicSalary * 0.12).toLocaleString()}</span>
                  </div>
                  <div className="flex justify-between text-red-600">
                    <span>ESI (1.75%):</span>
                    <span>-₹{((payrollData.basicSalary + payrollData.hra + payrollData.da) * 0.0175).toLocaleString()}</span>
                  </div>
                  <div className="border-t pt-2 flex justify-between font-bold text-lg text-green-600">
                    <span>Net Salary:</span>
                    <span>₹{(payrollData.netSalary - payrollData.basicSalary * 0.12 - (payrollData.basicSalary + payrollData.hra + payrollData.da) * 0.0175).toLocaleString()}</span>
                  </div>
                </div>
              </div>

              <button
                onClick={generatePayslip}
                className="w-full px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
              >
                Generate Payslip
              </button>
            </div>
          </div>
        )}
      </div>

      {/* Payroll Summary */}
      <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-lg shadow-md`}>
        <h3 className="text-lg font-semibold mb-4">Monthly Payroll Summary</h3>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div className="text-center">
            <p className="text-2xl font-bold text-blue-600">₹{employees.reduce((sum, emp) => sum + (emp.salary || 50000), 0).toLocaleString()}</p>
            <p className="text-sm text-gray-500">Total Basic Salary</p>
          </div>
          <div className="text-center">
            <p className="text-2xl font-bold text-green-600">₹{(employees.reduce((sum, emp) => sum + (emp.salary || 50000), 0) * 1.52).toLocaleString()}</p>
            <p className="text-sm text-gray-500">Gross Payroll</p>
          </div>
          <div className="text-center">
            <p className="text-2xl font-bold text-red-600">₹{(employees.reduce((sum, emp) => sum + (emp.salary || 50000), 0) * 0.35).toLocaleString()}</p>
            <p className="text-sm text-gray-500">Total Deductions</p>
          </div>
          <div className="text-center">
            <p className="text-2xl font-bold text-purple-600">₹{(employees.reduce((sum, emp) => sum + (emp.salary || 50000), 0) * 1.17).toLocaleString()}</p>
            <p className="text-sm text-gray-500">Net Payroll</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Payroll;
