import React, { useState, useEffect } from 'react';
import SimpleCalendar from './SimpleCalendar';
import CreativeCalendar from './CreativeCalendar';
import ProfessionalCalendar from './ProfessionalCalendar';

function AdminPanel() {
  const [theme, setTheme] = useState('light');
  const [activeTab, setActiveTab] = useState('dashboard');
  const [employees, setEmployees] = useState([]);
  const [holidays, setHolidays] = useState([]);
  const [leaveRequests, setLeaveRequests] = useState([]);
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [departmentFilter, setDepartmentFilter] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [showEmployeeDetails, setShowEmployeeDetails] = useState(false);

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    availableLeaves: 20,
    dateOfJoining: '',
    employeeId: '',
    dateOfBirth: '',
    department: '',
    position: '',
    role: '',
    phone: '',
    address: '',
    profilePhoto: ''
  });
  const [formErrors, setFormErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [holidayForm, setHolidayForm] = useState({
    name: '',
    date: '',
    year: new Date().getFullYear()
  });

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') || 'light';
    setTheme(savedTheme);
    document.documentElement.setAttribute('data-theme', savedTheme);
    fetchEmployees();
    fetchHolidays();
    fetchLeaveRequests();

    const handleStorageChange = (e) => {
      if (e.key === 'theme') {
        setTheme(e.newValue);
        document.documentElement.setAttribute('data-theme', e.newValue);
      }
    };
    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme);
    document.documentElement.setAttribute('data-theme', newTheme);
    window.dispatchEvent(new StorageEvent('storage', { key: 'theme', newValue: newTheme }));
  };

  const fetchEmployees = async () => {
    try {
      const response = await fetch('http://localhost:5002/api/employees');
      const data = await response.json();
      setEmployees(data);
    } catch (error) {
      console.error('Error fetching employees:', error);
    }
  };

  const fetchHolidays = async () => {
    try {
      const response = await fetch('http://localhost:5002/api/holidays');
      const data = await response.json();
      setHolidays(data);
    } catch (error) {
      console.error('Error fetching holidays:', error);
    }
  };

  const fetchLeaveRequests = async () => {
    try {
      const response = await fetch('http://localhost:5002/api/leave-requests');
      const data = await response.json();
      setLeaveRequests(data);
    } catch (error) {
      console.error('Error fetching leave requests:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    const updatedData = {
      ...formData,
      [name]: value
    };

    // Auto-generate employee ID and work email when name changes
    if (name === 'name' && value.trim()) {
      const nameForId = value.toLowerCase().replace(/[^a-z]/g, '');
      const timestamp = Date.now().toString().slice(-4);
      updatedData.employeeId = `EMP${nameForId.slice(0, 3).toUpperCase()}${timestamp}`;
      updatedData.email = `${value.toLowerCase().replace(/\s+/g, '.')}@czarcore.com`;
    }

    // Auto-generate password from DOB when date of birth changes
    if (name === 'dateOfBirth' && value) {
      const dob = new Date(value);
      const day = String(dob.getDate()).padStart(2, '0');
      const month = String(dob.getMonth() + 1).padStart(2, '0');
      const year = dob.getFullYear();
      updatedData.password = `${day}${month}${year}`;
    }

    setFormData(updatedData);
    if (formErrors[name]) {
      setFormErrors({
        ...formErrors,
        [name]: ''
      });
    }
  };

  const validateForm = () => {
    const errors = {};

    if (!formData.name.trim()) errors.name = 'Name is required';
    if (!formData.department.trim()) errors.department = 'Department is required';
    if (!formData.position.trim()) errors.position = 'Position is required';
    if (!formData.role.trim()) errors.role = 'Role is required';
    if (!formData.dateOfJoining) errors.dateOfJoining = 'Date of joining is required';
    if (!formData.dateOfBirth) errors.dateOfBirth = 'Date of birth is required';
    if (formData.availableLeaves < 0 || formData.availableLeaves > 30) errors.availableLeaves = 'Available leaves must be between 0 and 30';

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleHolidayChange = (e) => {
    setHolidayForm({
      ...holidayForm,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    setIsSubmitting(true);
    try {
      const token = localStorage.getItem('token');
      const url = editingEmployee 
        ? `http://localhost:5002/api/employees/${editingEmployee._id}`
        : 'http://localhost:5002/api/employees';
      const method = editingEmployee ? 'PUT' : 'POST';

      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        fetchEmployees();
        resetForm();
        alert(`Employee ${editingEmployee ? 'updated' : 'added'} successfully!`);
      } else {
        alert(data.message || 'Error saving employee');
      }
    } catch (error) {
      console.error('Error saving employee:', error);
      alert('Network error. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

