import React, { useState } from 'react';

const CreativeCalendar = ({ holidays = [], onAddHoliday, onDeleteHoliday, theme = 'light' }) => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [editingHoliday, setEditingHoliday] = useState(null);
  const [holidayForm, setHolidayForm] = useState({ name: '', date: '' });

  const monthThemes = {
    0: { gradient: 'from-blue-500 via-indigo-500 to-purple-600', bg: 'from-blue-50 to-indigo-100', name: 'January' },
    1: { gradient: 'from-pink-500 via-rose-500 to-red-500', bg: 'from-pink-50 to-rose-100', name: 'February' },
    2: { gradient: 'from-green-500 via-emerald-500 to-teal-500', bg: 'from-green-50 to-emerald-100', name: 'March' },
    3: { gradient: 'from-yellow-500 via-amber-500 to-orange-500', bg: 'from-yellow-50 to-amber-100', name: 'April' },
    4: { gradient: 'from-emerald-500 via-green-500 to-lime-500', bg: 'from-emerald-50 to-green-100', name: 'May' },
    5: { gradient: 'from-cyan-500 via-sky-500 to-blue-500', bg: 'from-cyan-50 to-sky-100', name: 'June' },
    6: { gradient: 'from-orange-500 via-red-500 to-pink-500', bg: 'from-orange-50 to-red-100', name: 'July' },
    7: { gradient: 'from-amber-500 via-yellow-500 to-lime-500', bg: 'from-amber-50 to-yellow-100', name: 'August' },
    8: { gradient: 'from-orange-500 via-amber-500 to-yellow-500', bg: 'from-orange-50 to-amber-100', name: 'September' },
    9: { gradient: 'from-red-500 via-orange-500 to-amber-500', bg: 'from-red-50 to-orange-100', name: 'October' },
    10: { gradient: 'from-gray-500 via-slate-500 to-zinc-500', bg: 'from-gray-50 to-slate-100', name: 'November' },
    11: { gradient: 'from-red-500 via-green-500 to-emerald-500', bg: 'from-red-50 to-green-100', name: 'December' }
  };

  const currentTheme = monthThemes[currentDate.getMonth()];

  const navigateMonth = (direction) => {
    const newDate = new Date(currentDate);
    newDate.setMonth(currentDate.getMonth() + direction);
    setCurrentDate(newDate);
  };

  const getHolidaysForDate = (day) => {
    const date = new Date(currentDate.getFullYear(), currentDate.getMonth(), day);
    return holidays.filter(h => new Date(h.date).toDateString() === date.toDateString());
  };

  const handleDateClick = (day) => {
    const date = new Date(currentDate.getFullYear(), currentDate.getMonth(), day);
    setSelectedDate(date);
    setShowModal(true);
    setHolidayForm({ name: '', date: date.toISOString().split('T')[0] });
  };

  const handleSaveHoliday = async () => {
    if (!holidayForm.name.trim()) return;

    const holidayData = {
      name: holidayForm.name,
      date: holidayForm.date,
      year: new Date(holidayForm.date).getFullYear()
    };

    if (editingHoliday) {
      // Update existing holiday
      try {
        const response = await fetch(`http://localhost:5002/api/holidays/${editingHoliday._id}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(holidayData)
        });
        if (response.ok && onAddHoliday) onAddHoliday();
      } catch (error) {
        console.error('Error updating holiday:', error);
      }
    } else {
      // Add new holiday
      if (onAddHoliday) onAddHoliday(holidayData);
    }

    setShowModal(false);
    setEditingHoliday(null);
    setHolidayForm({ name: '', date: '' });
  };

  const handleEditHoliday = (holiday) => {
    setEditingHoliday(holiday);
    setHolidayForm({
      name: holiday.name,
      date: new Date(holiday.date).toISOString().split('T')[0]
    });
    setShowModal(true);
  };

  const handleDeleteHoliday = (holidayId) => {
    if (window.confirm('Delete this holiday?')) {
      onDeleteHoliday(holidayId);
    }
  };

  const renderCalendarDays = () => {
    const daysInMonth = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0).getDate();
    const firstDay = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1).getDay();
    const days = [];
    const today = new Date();

    // Empty cells
    for (let i = 0; i < firstDay; i++) {
      days.push(<div key={`empty-${i}`} className="h-28"></div>);
    }

    // Days
    for (let day = 1; day <= daysInMonth; day++) {
      const date = new Date(currentDate.getFullYear(), currentDate.getMonth(), day);
      const dayHolidays = getHolidaysForDate(day);
      const isToday = date.toDateString() === today.toDateString();

      days.push(
        <div
          key={day}
          onClick={() => handleDateClick(day)}
          className={`h-28 p-3 rounded-2xl cursor-pointer transition-all duration-300 hover:scale-105 hover:shadow-2xl border-2 ${
            isToday 
              ? `bg-gradient-to-br ${currentTheme.gradient} text-white shadow-xl border-white/30` 
              : dayHolidays.length > 0
              ? `bg-gradient-to-br ${currentTheme.bg} border-green-300 shadow-lg`
              : theme === 'dark' 
              ? 'bg-gray-800 hover:bg-gray-700 border-gray-600' 
              : 'bg-white hover:bg-gray-50 border-gray-200 shadow-md'
          }`}
        >
          <div className={`font-bold text-lg mb-2 ${isToday ? 'text-white' : theme === 'dark' ? 'text-white' : 'text-gray-800'}`}>
            {day}
          </div>
          <div className="space-y-1">
            {dayHolidays.slice(0, 2).map((holiday) => (
              <div
                key={holiday._id}
                className="group flex items-center justify-between bg-green-500 text-white px-2 py-1 rounded-lg text-xs font-medium shadow-sm"
              >
                <span className="flex items-center gap-1 truncate">
                  🎉 {holiday.name}
                </span>
                <div className="flex gap-1 opacity-0 group-hover:opacity-100 transition-opacity">
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      handleEditHoliday(holiday);
                    }}
                    className="w-5 h-5 bg-blue-600 hover:bg-blue-700 rounded text-xs flex items-center justify-center"
                  >
                    ✏️
                  </button>
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      handleDeleteHoliday(holiday._id);
                    }}
                    className="w-5 h-5 bg-red-600 hover:bg-red-700 rounded text-xs flex items-center justify-center"
                  >
                    🗑️
                  </button>
                </div>
              </div>
            ))}
            {dayHolidays.length > 2 && (
              <div className="text-xs text-gray-500 font-medium">+{dayHolidays.length - 2} more</div>
            )}
          </div>
        </div>
      );
    }

    return days;
  };

  const upcomingHolidays = holidays
    .filter(h => new Date(h.date) >= new Date())
    .sort((a, b) => new Date(a.date) - new Date(b.date))
    .slice(0, 5);

  return (
    <div className={`min-h-screen p-8 ${theme === 'dark' ? 'bg-gray-900' : `bg-gradient-to-br ${currentTheme.bg}`}`}>
      <div className="max-w-7xl mx-auto">
        <div className="grid grid-cols-1 xl:grid-cols-4 gap-8">

          {/* Main Calendar */}
          <div className="xl:col-span-3">
            {/* Header */}
            <div className={`bg-gradient-to-r ${currentTheme.gradient} p-8 rounded-3xl text-white mb-8 shadow-2xl`}>
              <div className="flex items-center justify-between">
                <button
                  onClick={() => navigateMonth(-1)}
                  className="p-4 rounded-2xl bg-white/20 hover:bg-white/30 transition-all duration-300 backdrop-blur-sm"
                >
                  <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M12.707 5.293a1 1 0 010 1.414L9.414 10l3.293 3.293a1 1 0 01-1.414 1.414l-4-4a1 1 0 010-1.414l4-4a1 1 0 011.414 0z" clipRule="evenodd" />
                  </svg>
                </button>
                <div className="text-center">
                  <h1 className="text-5xl font-bold mb-2">{currentTheme.name}</h1>
                  <p className="text-xl opacity-90">{currentDate.getFullYear()}</p>
                </div>
                <button
                  onClick={() => navigateMonth(1)}
                  className="p-4 rounded-2xl bg-white/20 hover:bg-white/30 transition-all duration-300 backdrop-blur-sm"
                >
                  <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M7.293 14.707a1 1 0 010-1.414L10.586 10 7.293 6.707a1 1 0 011.414-1.414l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414 0z" clipRule="evenodd" />
                  </svg>
                </button>
              </div>
            </div>

            {/* Days Header */}
            <div className="grid grid-cols-7 gap-4 mb-6">
              {['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'].map(day => (
                <div key={day} className={`text-center font-bold py-4 rounded-xl ${theme === 'dark' ? 'text-white' : 'text-gray-700'}`}>
                  {day.slice(0, 3)}
                </div>
              ))}
            </div>

            {/* Calendar Grid */}
            <div className="grid grid-cols-7 gap-4">
              {renderCalendarDays()}
            </div>
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            {/* Upcoming Holidays */}
            <div className={`p-6 rounded-3xl ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} shadow-2xl`}>
              <h3 className="text-xl font-bold mb-4 flex items-center gap-2">
                🎉 Upcoming Holidays
              </h3>
              <div className="space-y-3">
                {upcomingHolidays.map((holiday) => (
                  <div key={holiday._id} className="p-3 bg-green-100 rounded-xl border-l-4 border-green-500">
                    <div className="font-semibold text-green-800">{holiday.name}</div>
                    <div className="text-sm text-green-600">
                      {new Date(holiday.date).toLocaleDateString()}
                    </div>
                  </div>
                ))}
                {upcomingHolidays.length === 0 && (
                  <p className="text-gray-500 text-center py-4">No upcoming holidays</p>
                )}
              </div>
            </div>

            {/* Quick Stats */}
            <div className={`p-6 rounded-3xl ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} shadow-2xl`}>
              <h3 className="text-xl font-bold mb-4">📊 Quick Stats</h3>
              <div className="space-y-3">
                <div className="flex justify-between items-center">
                  <span>Total Holidays</span>
                  <span className="font-bold text-2xl text-green-600">{holidays.length}</span>
                </div>
                <div className="flex justify-between items-center">
                  <span>This Month</span>
                  <span className="font-bold text-2xl text-blue-600">
                    {holidays.filter(h => {
                      const hDate = new Date(h.date);
                      return hDate.getMonth() === currentDate.getMonth() && hDate.getFullYear() === currentDate.getFullYear();
                    }).length}
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Holiday Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black/60 flex items-center justify-center z-50 backdrop-blur-sm">
          <div className={`p-8 rounded-3xl max-w-md w-full mx-4 ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} shadow-2xl`}>
            <h3 className="text-2xl font-bold mb-6">
              {editingHoliday ? '✏️ Edit Holiday' : '🎉 Add Holiday'}
            </h3>

            {/* Existing Holidays for Selected Date */}
            {selectedDate && (
              <div className="mb-6">
                <h4 className="font-semibold mb-3">📅 {selectedDate.toLocaleDateString()}</h4>
                {getHolidaysForDate(selectedDate.getDate()).map((holiday) => (
                  <div key={holiday._id} className="flex items-center justify-between p-3 bg-green-100 rounded-xl mb-2">
                    <span className="text-green-800 font-medium">🎉 {holiday.name}</span>
                    <div className="flex gap-2">
                      <button
                        onClick={() => handleEditHoliday(holiday)}
                        className="px-3 py-1 bg-blue-500 text-white rounded-lg text-sm hover:bg-blue-600"
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => handleDeleteHoliday(holiday._id)}
                        className="px-3 py-1 bg-red-500 text-white rounded-lg text-sm hover:bg-red-600"
                      >
                        Delete
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}

            {/* Holiday Form */}
            <div className="space-y-4">
              <input
                type="text"
                placeholder="Holiday name"
                value={holidayForm.name}
                onChange={(e) => setHolidayForm({...holidayForm, name: e.target.value})}
                className={`w-full p-4 rounded-xl border-2 ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-gray-50 border-gray-200'} focus:border-blue-500 transition-colors`}
              />
              <input
                type="date"
                value={holidayForm.date}
                onChange={(e) => setHolidayForm({...holidayForm, date: e.target.value})}
                className={`w-full p-4 rounded-xl border-2 ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-gray-50 border-gray-200'} focus:border-blue-500 transition-colors`}
              />
              <div className="flex gap-3 pt-4">
                <button
                  onClick={handleSaveHoliday}
                  className={`flex-1 py-4 bg-gradient-to-r ${currentTheme.gradient} text-white rounded-xl font-bold hover:shadow-lg transition-all duration-300`}
                >
                  {editingHoliday ? 'Update Holiday' : 'Add Holiday'}
                </button>
                <button
                  onClick={() => {
                    setShowModal(false);
                    setEditingHoliday(null);
                    setHolidayForm({ name: '', date: '' });
                  }}
                  className={`flex-1 py-4 rounded-xl font-bold transition-all duration-300 ${
                    theme === 'dark' ? 'bg-gray-600 hover:bg-gray-500' : 'bg-gray-200 hover:bg-gray-300'
                  }`}
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CreativeCalendar;
