import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AdminPanel() {
  const [activeTab, setActiveTab] = useState('employees');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    dateOfBirth: '',
    dateOfJoining: '',
    availableLeaves: '',
    department: '',
    position: ''
  });
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/admin/users', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUsers(response.data);
    } catch (error) {
      setMessage(error.response?.data?.message || 'Failed to fetch users');
    }
  };



  const handleEdit = (user) => {
    if (!user.hasProfile) {
      setMessage('User has not created their profile yet');
      return;
    }
    setSelectedUser(user);
    setFormData({
      name: user.profile.name,
      email: user.profile.email,
      dateOfBirth: user.profile.dateOfBirth?.split('T')[0] || '',
      dateOfJoining: user.profile.dateOfJoining?.split('T')[0] || '',
      availableLeaves: user.profile.availableLeaves,
      department: user.profile.department || '',
      position: user.profile.position || ''
    });
    setIsEditing(true);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.put(`http://localhost:5000/api/admin/employees/${selectedUser.profile._id}`, formData, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setMessage('Employee updated successfully');
      setIsEditing(false);
      setSelectedUser(null);
      fetchUsers();
    } catch (error) {
      setMessage(error.response?.data?.message || 'Update failed');
    }
  };

  const handleDelete = async (user) => {
    if (!user.hasProfile) {
      setMessage('Cannot delete user without profile');
      return;
    }
    if (window.confirm('Are you sure you want to delete this employee profile?')) {
      try {
        const token = localStorage.getItem('token');
        await axios.delete(`http://localhost:5000/api/admin/employees/${user.profile._id}`, {
          headers: { Authorization: `Bearer ${token}` }
        });

        setMessage('Employee profile deleted successfully');
        fetchUsers();
      } catch (error) {
        setMessage(error.response?.data?.message || 'Delete failed');
      }
    }
  };

  if (isEditing) {
    return (
      <div className="admin-panel">
        <h2>Edit Employee Profile</h2>
        <form onSubmit={handleSubmit} className="employee-form">
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Date of Birth:</label>
            <input
              type="date"
              name="dateOfBirth"
              value={formData.dateOfBirth}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Date of Joining:</label>
            <input
              type="date"
              name="dateOfJoining"
              value={formData.dateOfJoining}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Available Leaves:</label>
            <input
              type="number"
              name="availableLeaves"
              value={formData.availableLeaves}
              onChange={handleChange}
              min="0"
              required
            />
          </div>

          <div className="form-group">
            <label>Department:</label>
            <input
              type="text"
              name="department"
              value={formData.department}
              onChange={handleChange}
            />
          </div>

          <div className="form-group">
            <label>Position:</label>
            <input
              type="text"
              name="position"
              value={formData.position}
              onChange={handleChange}
            />
          </div>

          <div className="form-actions">
            <button type="submit" className="btn-primary">Update Employee</button>
            <button type="button" onClick={() => setIsEditing(false)} className="btn-secondary">
              Cancel
            </button>
          </div>
        </form>
        {message && <div className="message">{message}</div>}
      </div>
    );
  }

  const currentUser = JSON.parse(localStorage.getItem('user') || '{}');

  return (
    <div className="flex min-h-screen bg-gray-50">
      {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 z-50 w-64 bg-gray-800 transform ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'} transition-transform duration-300 ease-in-out lg:translate-x-0 lg:static lg:inset-0`}>
        <div className="flex items-center justify-center h-16 bg-gray-900">
          <h3 className="text-white text-lg font-semibold">Admin Panel</h3>
        </div>
        <nav className="mt-5 px-2">
          <button 
            className={`group flex items-center px-2 py-2 text-base font-medium rounded-md w-full text-left ${
              activeTab === 'dashboard' 
                ? 'bg-gray-900 text-white' 
                : 'text-gray-300 hover:bg-gray-700 hover:text-white'
            }`}
            onClick={() => setActiveTab('dashboard')}
          >
            Dashboard
          </button>
          <button 
            className={`group flex items-center px-2 py-2 text-base font-medium rounded-md w-full text-left mt-1 ${
              activeTab === 'employees' 
                ? 'bg-gray-900 text-white' 
                : 'text-gray-300 hover:bg-gray-700 hover:text-white'
            }`}
            onClick={() => setActiveTab('employees')}
          >
            Employee Management
          </button>
          <button 
            className={`group flex items-center px-2 py-2 text-base font-medium rounded-md w-full text-left mt-1 ${
              activeTab === 'settings' 
                ? 'bg-gray-900 text-white' 
                : 'text-gray-300 hover:bg-gray-700 hover:text-white'
            }`}
            onClick={() => setActiveTab('settings')}
          >
            Settings
          </button>
          <button 
            className="group flex items-center px-2 py-2 text-base font-medium rounded-md w-full text-left mt-1 text-gray-300 hover:bg-gray-700 hover:text-white"
            onClick={() => {
              localStorage.clear();
              window.location.href = '/';
            }}
          >
            Logout
          </button>
        </nav>
      </div>

      {/* Main Content */}
      <div className="flex-1 lg:ml-64">
        {/* Header */}
        <div className="bg-white shadow-sm border-b border-gray-200">
          <div className="px-4 sm:px-6 lg:px-8">
            <div className="flex justify-between h-16">
              <div className="flex items-center">
                <button
                  className="lg:hidden p-2 rounded-md text-gray-400 hover:text-gray-500 hover:bg-gray-100"
                  onClick={() => setSidebarOpen(!sidebarOpen)}
                >
                  <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                  </svg>
                </button>
              </div>
              <div className="flex items-center space-x-4">
                <span className="text-gray-700 font-medium">Welcome, {currentUser.name || 'Admin'}</span>
                <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">
                  ADMIN
                </span>
                <button 
                  className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-md text-sm font-medium transition-colors"
                  onClick={() => {
                    localStorage.clear();
                    window.location.href = '/';
                  }}
                >
                  Logout
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Content Area */}
        <div className="p-6">
          {activeTab === 'employees' && (
            <div>
              <h2 className="text-2xl font-bold text-gray-900 mb-6">Employee Management</h2>
              <div className="bg-white shadow overflow-hidden sm:rounded-lg">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Name</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Email</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Employee ID</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Department</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Joined</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {users.filter(user => user.role === 'employee').map((user, index) => (
                      <tr key={user._id} className={index % 2 === 0 ? 'bg-white' : 'bg-gray-50'}>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{user.name}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{user.email}</td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                            user.hasProfile 
                              ? 'bg-green-100 text-green-800' 
                              : 'bg-yellow-100 text-yellow-800'
                          }`}>
                            {user.hasProfile ? 'Profile Complete' : 'Profile Pending'}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{user.profile.employeeId}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{user.profile.department}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{new Date(user.createdAt).toLocaleDateString()}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                          {user.hasProfile ? (
                            <div className="flex space-x-2">
                              <button 
                                onClick={() => handleEdit(user)}
                                className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded text-xs font-medium transition-colors"
                              >
                                Edit
                              </button>
                              <button 
                                onClick={() => handleDelete(user)}
                                className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded text-xs font-medium transition-colors"
                              >
                                Delete
                              </button>
                            </div>
                          ) : (
                            <span className="text-gray-400 italic text-sm">Waiting for profile</span>
                          )}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>

                {users.filter(user => user.role === 'employee').length === 0 && (
                  <div className="text-center py-12">
                    <p className="text-gray-500">No employees found.</p>
                  </div>
                )}
              </div>
            </div>
          )}

          {activeTab === 'dashboard' && (
            <div>
              <h2 className="text-2xl font-bold text-gray-900 mb-6">Dashboard Overview</h2>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <div className="bg-white overflow-hidden shadow rounded-lg">
                  <div className="p-5">
                    <div className="flex items-center">
                      <div className="flex-shrink-0">
                        <div className="text-3xl font-bold text-blue-600">{users.filter(u => u.role === 'employee').length}</div>
                      </div>
                    </div>
                    <div className="mt-1">
                      <p className="text-sm font-medium text-gray-500">Total Employees</p>
                    </div>
                  </div>
                </div>
                <div className="bg-white overflow-hidden shadow rounded-lg">
                  <div className="p-5">
                    <div className="flex items-center">
                      <div className="flex-shrink-0">
                        <div className="text-3xl font-bold text-green-600">{users.filter(u => u.hasProfile).length}</div>
                      </div>
                    </div>
                    <div className="mt-1">
                      <p className="text-sm font-medium text-gray-500">Complete Profiles</p>
                    </div>
                  </div>
                </div>
                <div className="bg-white overflow-hidden shadow rounded-lg">
                  <div className="p-5">
                    <div className="flex items-center">
                      <div className="flex-shrink-0">
                        <div className="text-3xl font-bold text-yellow-600">{users.filter(u => !u.hasProfile && u.role === 'employee').length}</div>
                      </div>
                    </div>
                    <div className="mt-1">
                      <p className="text-sm font-medium text-gray-500">Pending Profiles</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'settings' && (
            <div>
              <h2 className="text-2xl font-bold text-gray-900 mb-6">Settings</h2>
              <div className="bg-white shadow rounded-lg p-6">
                <p className="text-gray-500">Settings panel coming soon...</p>
              </div>
            </div>
          )}
        </div>
      </div>

      {message && (
        <div className="fixed bottom-4 right-4 bg-green-500 text-white px-6 py-3 rounded-lg shadow-lg z-50">
          {message}
        </div>
      )}
    </div>
  );
}

export default AdminPanel;
