import React, { useState, useEffect } from 'react';
import AdminPanel from './AdminPanel';
import EmployeeDashboard from './EmployeeDashboard';

function Dashboard() {
  const [user, setUser] = useState(null);
  const [theme, setTheme] = useState('light');
  const [activeTab, setActiveTab] = useState('overview');

  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (userData) {
      setUser(JSON.parse(userData));
    } else {
      window.location.href = '/auth';
    }

    const savedTheme = localStorage.getItem('theme') || 'light';
    setTheme(savedTheme);
    document.documentElement.setAttribute('data-theme', savedTheme);
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme);
    document.documentElement.setAttribute('data-theme', newTheme);
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  console.log('Current user role:', user.role);

  if (user.role === 'admin') {
    return <AdminPanel />;
  }

  return (
    <div className={`min-h-screen ${theme === 'dark' ? 'bg-gray-900 text-white' : 'bg-gray-50 text-gray-900'}`}>
      <div className="flex">
        <div className={`w-64 ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} shadow-lg h-screen fixed`}>
          <div className="p-6">
            <h2 className="text-xl font-bold text-indigo-600 mb-8">Dashboard</h2>
            <nav className="space-y-2">
              <button
                onClick={() => setActiveTab('overview')}
                className={`w-full text-left px-4 py-2 rounded-lg transition-colors ${
                  activeTab === 'overview'
                    ? 'bg-indigo-600 text-white'
                    : theme === 'dark'
                    ? 'text-gray-300 hover:bg-gray-700'
                    : 'text-gray-600 hover:bg-gray-100'
                }`}
              >
                Overview
              </button>
              <button
                onClick={() => setActiveTab('profile')}
                className={`w-full text-left px-4 py-2 rounded-lg transition-colors ${
                  activeTab === 'profile'
                    ? 'bg-indigo-600 text-white'
                    : theme === 'dark'
                    ? 'text-gray-300 hover:bg-gray-700'
                    : 'text-gray-600 hover:bg-gray-100'
                }`}
              >
                Profile
              </button>
              <button
                onClick={() => setActiveTab('leave')}
                className={`w-full text-left px-4 py-2 rounded-lg transition-colors ${
                  activeTab === 'leave'
                    ? 'bg-indigo-600 text-white'
                    : theme === 'dark'
                    ? 'text-gray-300 hover:bg-gray-700'
                    : 'text-gray-600 hover:bg-gray-100'
                }`}
              >
                Leave Requests
              </button>
              <button
                onClick={() => setActiveTab('holidays')}
                className={`w-full text-left px-4 py-2 rounded-lg transition-colors ${
                  activeTab === 'holidays'
                    ? 'bg-indigo-600 text-white'
                    : theme === 'dark'
                    ? 'text-gray-300 hover:bg-gray-700'
                    : 'text-gray-600 hover:bg-gray-100'
                }`}
              >
                Holidays
              </button>
            </nav>
          </div>
          <div className="absolute bottom-4 left-4">
            <button
              onClick={toggleTheme}
              className={`p-2 rounded-lg transition-colors ${
                theme === 'dark'
                  ? 'bg-gray-700 text-yellow-400 hover:bg-gray-600'
                  : 'bg-gray-200 text-gray-600 hover:bg-gray-300'
              }`}
            >
              {theme === 'dark' ? '☀️' : '🌙'}
            </button>
          </div>
        </div>

        <div className="ml-64 flex-1 p-8">
          <div className="flex justify-between items-center mb-8">
            <h1 className="text-2xl font-bold">
              {activeTab === 'overview' && 'Overview'}
              {activeTab === 'profile' && 'Profile'}
              {activeTab === 'leave' && 'Leave Requests'}
              {activeTab === 'holidays' && 'Holidays'}
            </h1>
            <div className="text-sm text-gray-500">Role: {user.role}</div>
            <button
              onClick={() => {
                localStorage.removeItem('user');
                window.location.href = '/auth';
              }}
              className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
            >
              Logout
            </button>
          </div>

          {activeTab === 'overview' && (
            <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-lg shadow-md`}>
              <h2 className="text-xl font-semibold mb-4">Welcome, {user.name}!</h2>
              <p className={theme === 'dark' ? 'text-gray-300' : 'text-gray-600'}>
                This is your employee dashboard overview.
              </p>
            </div>
          )}

          {activeTab === 'profile' && (
            <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-lg shadow-md`}>
              <h2 className="text-xl font-semibold mb-4">Employee Profile</h2>
              <p className={theme === 'dark' ? 'text-gray-300' : 'text-gray-600'}>
                Profile information will be displayed here.
              </p>
            </div>
          )}

          {activeTab === 'leave' && (
            <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-lg shadow-md`}>
              <h2 className="text-xl font-semibold mb-4">Leave Requests</h2>
              <p className={theme === 'dark' ? 'text-gray-300' : 'text-gray-600'}>
                Leave request functionality coming soon.
              </p>
            </div>
          )}

          {activeTab === 'holidays' && (
            <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-lg shadow-md`}>
              <h2 className="text-xl font-semibold mb-4">Holiday Calendar</h2>
              <p className={theme === 'dark' ? 'text-gray-300' : 'text-gray-600'}>
                Holiday calendar coming soon.
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
