import React, { useState, useEffect } from 'react';
import axios from 'axios';
import EmployeeProfile from './EmployeeProfile';
import AdminPanel from './AdminPanel';
import './Dashboard.css';

function Dashboard() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [adminCredentials, setAdminCredentials] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');
    const adminCreds = localStorage.getItem('adminCredentials');

    console.log('Dashboard - Token:', token);
    console.log('Dashboard - User Data:', userData);
    console.log('Dashboard - Admin Creds:', adminCreds);

    if (token && userData) {
      try {
        setUser(JSON.parse(userData));
      } catch (error) {
        console.error('Error parsing user data:', error);
      }
    }

    if (adminCreds) {
      try {
        const creds = JSON.parse(adminCreds);
        console.log('Parsed admin credentials:', creds);
        setAdminCredentials(creds);
      } catch (error) {
        console.error('Error parsing admin credentials:', error);
      }
    }

    setLoading(false);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/';
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (!user) {
    return (
      <div className="dashboard">
        <div className="not-logged-in">
          <h2>Please log in to access the dashboard</h2>
          <button onClick={() => window.location.href = '/auth'}>
            Go to Login
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <h1>Dashboard</h1>
        <div className="user-info">
          <span>Welcome, {user.name}</span>
          <span className="role-badge">{user.role || 'employee'}</span>
          <button onClick={handleLogout} className="logout-btn">
            Logout
          </button>
        </div>
      </header>

      <main className="dashboard-content">


        {user.role === 'admin' ? (
          <AdminPanel />
        ) : (
          <EmployeeProfile />
        )}
      </main>
    </div>
  );
}

export default Dashboard;
