import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AuthPage.css';

function AuthPage() {
  const [isSignUp, setIsSignUp] = useState(false);
  const [showForgotPassword, setShowForgotPassword] = useState(false);
  const [formData, setFormData] = useState({ 
    name: '', email: '', password: '', role: 'employee',
    employeeId: '', dateOfBirth: '', dateOfJoining: '', availableLeaves: '20'
  });
  const [forgotEmail, setForgotEmail] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const token = urlParams.get('token');
    const user = urlParams.get('user');

    if (token && user) {
      localStorage.setItem('token', token);
      localStorage.setItem('user', user);
      setMessage('Google login successful!');
      window.history.replaceState({}, document.title, '/auth');
    }
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const endpoint = isSignUp ? 'http://localhost:5000/api/register' : 'http://localhost:5000/api/login';
      const data = isSignUp ? formData : { email: formData.email, password: formData.password };

      const response = await axios.post(endpoint, data);

      if (isSignUp && formData.role === 'admin') {
        localStorage.setItem('token', response.data.token);
        localStorage.setItem('user', JSON.stringify(response.data.user));
        localStorage.setItem('adminCredentials', JSON.stringify(response.data.workCredentials));
        setMessage(
          <div>
            <strong>Admin Registration Successful!</strong><br/>
            Work Email: {response.data.workCredentials.workEmail}<br/>
            Work Password: {response.data.workCredentials.workPassword}<br/>
            <small>Redirecting to dashboard...</small>
          </div>
        );
        setTimeout(() => {
          window.location.href = '/dashboard';
        }, 2000);
      } else {
        localStorage.setItem('token', response.data.token);
        localStorage.setItem('user', JSON.stringify(response.data.user));
        setMessage(`${isSignUp ? 'Registration' : 'Login'} successful!`);

        setTimeout(() => {
          window.location.href = '/dashboard';
        }, 1000);
      }
    } catch (error) {
      const errorData = error.response?.data;
      setMessage(errorData?.message || 'Operation failed');
    }
  };

  const handleForgotPassword = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/forgot-password', { email: forgotEmail });
      setMessage(response.data.message);
      setShowForgotPassword(false);
    } catch (error) {
      setMessage(error.response?.data?.message || 'Failed to send reset email');
    }
  };

  const handleGoogleLogin = (e) => {
    e.preventDefault();
    e.stopPropagation();
    window.location.href = 'http://localhost:5000/api/auth/google';
  };

  const toggleMode = () => {
    setIsSignUp(!isSignUp);
    setFormData({ 
      name: '', email: '', password: '', role: 'employee',
      employeeId: '', dateOfBirth: '', dateOfJoining: '', availableLeaves: '20'
    });
    setMessage('');
    setShowForgotPassword(false);
  };

  const showForgotForm = (e) => {
    e.preventDefault();
    setShowForgotPassword(true);
    setMessage('');
  };

  const hideForgotForm = () => {
    setShowForgotPassword(false);
    setForgotEmail('');
    setMessage('');
  };

  if (showForgotPassword) {
    return (
      <div className="auth-container">
        <div className="auth-wrapper">
          <div className="forms-container">
            <div className="signin-signup">
              <form className="sign-in-form" onSubmit={handleForgotPassword}>
                <h2 className="title">Forgot Password</h2>
                <p style={{ marginBottom: '20px', color: '#666' }}>
                  Enter your email to receive a password reset link
                </p>

                <div className="input-field">
                  <input
                    type="email"
                    placeholder="Enter your email"
                    value={forgotEmail}
                    onChange={(e) => setForgotEmail(e.target.value)}
                    required
                  />
                </div>

                <button type="submit" className="btn solid">Send Reset Link</button>

                <button type="button" className="btn transparent" onClick={hideForgotForm}>
                  Back to Login
                </button>
              </form>
            </div>
          </div>
        </div>
        {message && <div className="message">{message}</div>}
      </div>
    );
  }

  return (
    <div className="auth-container">
      <div className={`auth-wrapper ${isSignUp ? 'sign-up-mode' : ''}`}>
        <div className="forms-container">
          <div className="signin-signup">
            <form className={`sign-in-form ${!isSignUp ? 'active' : ''}`} onSubmit={handleSubmit}>
              <h2 className="title">Sign In</h2>

              <div className="role-toggle">
                <button
                  type="button"
                  className={`role-btn ${formData.role === 'employee' ? 'active' : ''}`}
                  onClick={() => setFormData({...formData, role: 'employee'})}
                >
                  USER
                </button>
                <span className="role-or">OR</span>
                <button
                  type="button"
                  className={`role-btn ${formData.role === 'admin' ? 'active' : ''}`}
                  onClick={() => setFormData({...formData, role: 'admin'})}
                >
                  ADMIN
                </button>
              </div>

              <div className="input-field">
                <input
                  type="email"
                  name="email"
                  placeholder="Email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="input-field">
                <input
                  type="password"
                  name="password"
                  placeholder="Password"
                  value={formData.password}
                  onChange={handleChange}
                  required
                />
              </div>

              <button type="submit" className="btn solid">Sign In</button>

              <button type="button" className="google-btn" onClick={handleGoogleLogin}>
                <div className="google-icon">G</div>
                Continue with Google
              </button>

              <a href="#" className="forgot-password" onClick={showForgotForm}>
                Forgot Password?
              </a>
            </form>

            <form className={`sign-up-form ${isSignUp ? 'active' : ''}`} onSubmit={handleSubmit}>
              <h2 className="title">Create Account</h2>

              <div className="role-toggle">
                <button
                  type="button"
                  className={`role-btn ${formData.role === 'employee' ? 'active' : ''}`}
                  onClick={() => setFormData({...formData, role: 'employee'})}
                >
                  USER
                </button>
                <span className="role-or">OR</span>
                <button
                  type="button"
                  className={`role-btn ${formData.role === 'admin' ? 'active' : ''}`}
                  onClick={() => setFormData({...formData, role: 'admin'})}
                >
                  ADMIN
                </button>
              </div>

              {formData.role === 'employee' && (
                <>
                  <div className="input-field">
                    <input
                      type="text"
                      name="name"
                      placeholder="Full Name"
                      value={formData.name}
                      onChange={handleChange}
                      required
                    />
                  </div>
                  <div className="input-field">
                    <input
                      type="email"
                      name="email"
                      placeholder="Personal Email"
                      value={formData.email}
                      onChange={handleChange}
                      required
                    />
                  </div>
                  <div className="input-field">
                    <input
                      type="password"
                      name="password"
                      placeholder="Password"
                      value={formData.password}
                      onChange={handleChange}
                      required
                    />
                  </div>
                  <div className="input-field">
                    <input
                      type="text"
                      name="employeeId"
                      placeholder="Employee ID"
                      value={formData.employeeId}
                      onChange={handleChange}
                      required
                    />
                  </div>
                  <div className="input-field">
                    <input
                      type="date"
                      name="dateOfBirth"
                      placeholder="Date of Birth"
                      value={formData.dateOfBirth}
                      onChange={handleChange}
                      required
                    />
                  </div>
                  <div className="input-field">
                    <input
                      type="date"
                      name="dateOfJoining"
                      placeholder="Date of Joining"
                      value={formData.dateOfJoining}
                      onChange={handleChange}
                      required
                    />
                  </div>
                  <div className="input-field">
                    <input
                      type="number"
                      name="availableLeaves"
                      placeholder="Available Leaves"
                      value={formData.availableLeaves}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </>
              )}

              {formData.role === 'admin' && (
                <>
                  <div className="input-field">
                    <input
                      type="text"
                      name="name"
                      placeholder="Full Name"
                      value={formData.name}
                      onChange={handleChange}
                      required
                    />
                  </div>
                  <div className="input-field">
                    <input
                      type="email"
                      name="email"
                      placeholder="Personal Email"
                      value={formData.email}
                      onChange={handleChange}
                      required
                    />
                  </div>
                  <div className="admin-note">
                    <p>Work email and password will be provided after registration.</p>
                  </div>
                </>
              )}

              <button type="submit" className="btn solid">Sign Up</button>

              <button type="button" className="google-btn" onClick={handleGoogleLogin}>
                <div className="google-icon">G</div>
                Continue with Google
              </button>
            </form>
          </div>
        </div>

        <div className="panels-container">
          <div className="panel left-panel">
            <div className="content">
              <h3>Hello, Friend!</h3>
              <p>Fill up personal information and start journey with us</p>
              <button className="btn transparent" onClick={toggleMode}>
                SIGN UP
              </button>
            </div>
          </div>

          <div className="panel right-panel">
            <div className="content">
              <h3>Welcome Back!</h3>
              <p>Enter your personal details to use all of site features</p>
              <button className="btn transparent" onClick={toggleMode}>
                SIGN IN
              </button>
            </div>
          </div>
        </div>
      </div>

      {message && <div className="message">{message}</div>}
    </div>
  );
}

export default AuthPage;
