import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AuthPage() {
  const [isSignUp, setIsSignUp] = useState(false);
  const [showForgotPassword, setShowForgotPassword] = useState(false);
  const [formData, setFormData] = useState({ name: '', email: '', password: '', role: 'employee' });
  const [forgotEmail, setForgotEmail] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const token = urlParams.get('token');
    const user = urlParams.get('user');

    if (token && user) {
      localStorage.setItem('token', token);
      localStorage.setItem('user', user);
      setMessage('Google login successful!');
      window.history.replaceState({}, document.title, '/auth');
    }
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const endpoint = isSignUp ? 'http://localhost:5000/api/register' : 'http://localhost:5000/api/login';
      const data = isSignUp ? formData : { email: formData.email, password: formData.password };

      const response = await axios.post(endpoint, data);
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('user', JSON.stringify(response.data.user));
      setMessage(`${isSignUp ? 'Registration' : 'Login'} successful!`);

      setTimeout(() => {
        window.location.href = '/dashboard';
      }, 1000);
    } catch (error) {
      setMessage(error.response?.data?.message || 'Operation failed');
    }
  };

  const handleForgotPassword = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/forgot-password', { email: forgotEmail });
      setMessage(response.data.message);
      setShowForgotPassword(false);
    } catch (error) {
      setMessage(error.response?.data?.message || 'Failed to send reset email');
    }
  };

  const handleGoogleLogin = (e) => {
    e.preventDefault();
    e.stopPropagation();
    window.location.href = 'http://localhost:5000/api/auth/google';
  };

  const toggleMode = () => {
    setIsSignUp(!isSignUp);
    setFormData({ name: '', email: '', password: '', role: 'employee' });
    setMessage('');
    setShowForgotPassword(false);
  };

  if (showForgotPassword) {
    return (
      <div className="min-h-screen bg-white flex items-center justify-center pt-20">
        <div className="bg-white p-8 rounded-2xl shadow-2xl max-w-md w-full">
          <form onSubmit={handleForgotPassword} className="space-y-6">
            <h2 className="text-2xl font-bold text-gray-800 text-center">Forgot Password</h2>
            <p className="text-gray-600 text-center">Enter your email to receive a password reset link</p>

            <div className="bg-gray-100 rounded-full h-12 flex items-center px-4">
              <input
                type="email"
                placeholder="Enter your email"
                value={forgotEmail}
                onChange={(e) => setForgotEmail(e.target.value)}
                className="bg-transparent outline-none flex-1 text-gray-700 font-medium"
                required
              />
            </div>

            <button type="submit" className="w-full bg-blue-500 text-white py-3 rounded-full font-semibold hover:bg-blue-600 transition">
              Send Reset Link
            </button>

            <button type="button" onClick={() => setShowForgotPassword(false)} className="w-full border-2 border-gray-300 text-gray-700 py-3 rounded-full font-semibold hover:bg-gray-50 transition">
              Back to Login
            </button>
          </form>
          {message && <div className="mt-4 p-3 bg-green-100 text-green-700 rounded text-center">{message}</div>}
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white flex items-center justify-center pt-20">
      {/* Centered Login Card */}
      <div className="relative w-[1100px] h-[650px] bg-white rounded-2xl shadow-2xl overflow-hidden">
        {/* Animated Background Circle for curved coverage */}
        <div className={`absolute w-[1400px] h-[1400px] rounded-full bg-gradient-to-br from-blue-500 to-blue-600 transition-all duration-[1800ms] ease-in-out z-10 ${
          isSignUp 
            ? 'top-0 right-[52%] translate-x-full -translate-y-1/2' 
            : 'top-0 right-[48%] -translate-y-1/2'
        }`}></div>
        {/* Forms Container */}
        <div className="absolute w-full h-full top-0 left-0">
          <div className={`absolute top-1/2 -translate-y-1/2 w-1/2 transition-all duration-1000 delay-700 ease-in-out z-20 ${
            isSignUp ? 'left-1/4 -translate-x-1/2' : 'left-3/4 -translate-x-1/2'
          }`}>

            {/* Sign In Form */}
            <div className={`flex flex-col items-center justify-center px-12 py-8 transition-all duration-200 delay-700 ${
              isSignUp ? 'opacity-0 z-10' : 'opacity-100 z-20'
            }`}>
              <h2 className="text-2xl font-bold text-gray-700 mb-6">Sign In</h2>

              <form onSubmit={handleSubmit} className="w-full max-w-xs space-y-3">
                <div className="bg-gray-100 rounded-full h-12 flex items-center px-4">
                  <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={formData.email}
                    onChange={handleChange}
                    className="bg-transparent outline-none flex-1 text-gray-700 font-medium text-sm"
                    required
                  />
                </div>

                <div className="bg-gray-100 rounded-full h-12 flex items-center px-4">
                  <input
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={formData.password}
                    onChange={handleChange}
                    className="bg-transparent outline-none flex-1 text-gray-700 font-medium text-sm"
                    required
                  />
                </div>

                <button type="submit" className="w-32 bg-blue-500 text-white py-2.5 rounded-full font-bold uppercase tracking-wide hover:bg-blue-600 transition text-xs mx-auto block mt-4">
                  Sign In
                </button>
              </form>

              <button type="button" onClick={handleGoogleLogin} className="w-full max-w-xs bg-white border-2 border-gray-300 text-gray-700 py-2.5 rounded-full font-medium hover:border-blue-500 hover:text-blue-500 transition mt-3 flex items-center justify-center gap-2 text-sm">
                <div className="w-4 h-4 bg-blue-500 text-white rounded-full flex items-center justify-center text-xs font-bold">G</div>
                Continue with Google
              </button>

              <button type="button" onClick={() => setShowForgotPassword(true)} className="text-blue-500 text-sm mt-3 hover:underline">
                Forgot Password?
              </button>
            </div>

            {/* Sign Up Form */}
            <div className={`flex flex-col items-center justify-center px-12 py-8 transition-all duration-200 delay-700 absolute top-0 left-0 w-full ${
              isSignUp ? 'opacity-100 z-20' : 'opacity-0 z-10'
            }`}>
              <h2 className="text-2xl font-bold text-gray-700 mb-6">Create Account</h2>

              <form onSubmit={handleSubmit} className="w-full max-w-xs space-y-2">
                <div className="bg-gray-100 rounded-full h-11 flex items-center px-4">
                  <input
                    type="text"
                    name="name"
                    placeholder="Name"
                    value={formData.name}
                    onChange={handleChange}
                    className="bg-transparent outline-none flex-1 text-gray-700 font-medium text-sm"
                    required
                  />
                </div>

                <div className="bg-gray-100 rounded-full h-11 flex items-center px-4">
                  <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={formData.email}
                    onChange={handleChange}
                    className="bg-transparent outline-none flex-1 text-gray-700 font-medium text-sm"
                    required
                  />
                </div>

                <div className="bg-gray-100 rounded-full h-11 flex items-center px-4">
                  <input
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={formData.password}
                    onChange={handleChange}
                    className="bg-transparent outline-none flex-1 text-gray-700 font-medium text-sm"
                    required
                  />
                </div>

                <div className="bg-gray-100 rounded-full h-11 flex items-center px-4">
                  <select
                    name="role"
                    value={formData.role}
                    onChange={handleChange}
                    className="bg-transparent outline-none flex-1 text-gray-700 font-medium cursor-pointer text-sm"
                    required
                  >
                    <option value="employee">Employee</option>
                    <option value="admin">Admin</option>
                  </select>
                </div>

                <button type="submit" className="w-32 bg-blue-500 text-white py-2.5 rounded-full font-bold uppercase tracking-wide hover:bg-blue-600 transition text-xs mx-auto block mt-4">
                  Sign Up
                </button>
              </form>

              <button type="button" onClick={handleGoogleLogin} className="w-full max-w-xs bg-white border-2 border-gray-300 text-gray-700 py-2.5 rounded-full font-medium hover:border-blue-500 hover:text-blue-500 transition mt-3 flex items-center justify-center gap-2 text-sm">
                <div className="w-4 h-4 bg-blue-500 text-white rounded-full flex items-center justify-center text-xs font-bold">G</div>
                Continue with Google
              </button>
            </div>
          </div>
        </div>

        {/* Panels */}
        <div className="absolute h-full w-full top-0 left-0 grid grid-cols-2 z-20">
          {/* Left Panel */}
          <div className={`flex flex-col items-end justify-center text-center px-8 py-12 ${
            isSignUp ? 'pointer-events-none' : 'pointer-events-auto'
          }`}>
            <div className={`text-white transition-transform duration-900 delay-600 ease-in-out ${
              isSignUp ? '-translate-x-[800px]' : 'translate-x-0'
            }`}>
              <h3 className="text-xl font-semibold mb-3">Hello, Friend!</h3>
              <p className="text-sm mb-4 opacity-90 leading-relaxed">Fill up personal information and start journey with us</p>
              <button onClick={toggleMode} className="bg-transparent border-2 border-white text-white px-6 py-2 rounded-full font-semibold text-xs hover:bg-white hover:text-blue-500 transition">
                SIGN UP
              </button>
            </div>
          </div>

          {/* Right Panel */}
          <div className={`flex flex-col items-start justify-center text-center px-8 py-12 ${
            isSignUp ? 'pointer-events-auto' : 'pointer-events-none'
          }`}>
            <div className={`text-white transition-transform duration-900 delay-600 ease-in-out ${
              isSignUp ? 'translate-x-0' : 'translate-x-[800px]'
            }`}>
              <h3 className="text-xl font-semibold mb-3">Welcome Back!</h3>
              <p className="text-sm mb-4 opacity-90 leading-relaxed">Enter your personal details to use all of site features</p>
              <button onClick={toggleMode} className="bg-transparent border-2 border-white text-white px-6 py-2 rounded-full font-semibold text-xs hover:bg-white hover:text-blue-500 transition">
                SIGN IN
              </button>
            </div>
          </div>
        </div>

        {message && (
          <div className={`absolute bottom-4 left-1/2 -translate-x-1/2 px-4 py-2 rounded-lg z-30 text-sm ${
            message.includes('successful') ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'
          }`}>
            {message}
          </div>
        )}
      </div>
    </div>
  );
}

export default AuthPage;
