import React, { useState, useEffect } from 'react';
import axios from 'axios';
import EmployeeProfile from './EmployeeProfile.jsx';
import AdminPanel from './AdminPanel.jsx';

function Dashboard() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const [stats, setStats] = useState({
    totalEmployees: 0,
    activeProjects: 0,
    pendingLeaves: 0,
    completedTasks: 0
  });

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');

    if (token && userData) {
      try {
        setUser(JSON.parse(userData));
        fetchDashboardStats();
      } catch (error) {
        console.error('Error parsing user data:', error);
      }
    }
    setLoading(false);
  }, []);

  const fetchDashboardStats = async () => {
    try {
      const token = localStorage.getItem('token');
      // Simulate API call for stats
      setStats({
        totalEmployees: 248,
        activeProjects: 12,
        pendingLeaves: 8,
        completedTasks: 156
      });
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/';
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center">
        <div className="bg-white p-8 rounded-2xl shadow-xl">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="text-gray-600 mt-4 text-center">Loading Dashboard...</p>
        </div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center">
        <div className="bg-white p-8 rounded-2xl shadow-xl text-center max-w-md">
          <div className="text-6xl mb-4">🔒</div>
          <h2 className="text-2xl font-bold text-gray-900 mb-4">Access Denied</h2>
          <p className="text-gray-600 mb-6">Please log in to access your dashboard</p>
          <button 
            onClick={() => window.location.href = '/auth'} 
            className="bg-gradient-to-r from-blue-600 to-indigo-600 text-white px-8 py-3 rounded-full font-semibold hover:from-blue-700 hover:to-indigo-700 transition-all duration-300 transform hover:scale-105"
          >
            Go to Login
          </button>
        </div>
      </div>
    );
  }

  const StatCard = ({ title, value, icon, color, trend }) => (
    <div className="bg-white rounded-2xl p-6 shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1">
      <div className="flex items-center justify-between">
        <div>
          <p className="text-gray-500 text-sm font-medium">{title}</p>
          <p className="text-3xl font-bold text-gray-900 mt-2">{value}</p>
          {trend && (
            <p className={`text-sm mt-2 ${trend > 0 ? 'text-green-600' : 'text-red-600'}`}>
              {trend > 0 ? '↗' : '↘'} {Math.abs(trend)}% from last month
            </p>
          )}
        </div>
        <div className={`p-4 rounded-2xl ${color}`}>
          <span className="text-2xl">{icon}</span>
        </div>
      </div>
    </div>
  );

  const TabButton = ({ id, label, isActive, onClick }) => (
    <button
      onClick={() => onClick(id)}
      className={`px-6 py-3 rounded-full font-semibold transition-all duration-300 ${
        isActive
          ? 'bg-gradient-to-r from-blue-600 to-indigo-600 text-white shadow-lg'
          : 'text-gray-600 hover:text-blue-600 hover:bg-blue-50'
      }`}
    >
      {label}
    </button>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
      {/* Header */}
      <header className="bg-white/80 backdrop-blur-md shadow-lg border-b border-white/20 sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-6 py-4">
          <div className="flex justify-between items-center">
            <div className="flex items-center space-x-4">
              <div className="bg-gradient-to-r from-blue-600 to-indigo-600 p-3 rounded-2xl">
                <span className="text-white text-xl font-bold">CZ</span>
              </div>
              <div>
                <h1 className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent">
                  Dashboard
                </h1>
                <p className="text-gray-500 text-sm">Welcome back, {user.name}</p>
              </div>
            </div>

            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-3">
                <div className="w-10 h-10 bg-gradient-to-r from-blue-600 to-indigo-600 rounded-full flex items-center justify-center text-white font-semibold">
                  {user.name.charAt(0).toUpperCase()}
                </div>
                <div className="hidden md:block">
                  <p className="font-semibold text-gray-900">{user.name}</p>
                  <p className={`text-xs px-2 py-1 rounded-full ${
                    user.role === 'admin' 
                      ? 'bg-red-100 text-red-800' 
                      : 'bg-blue-100 text-blue-800'
                  }`}>
                    {(user.role || 'employee').toUpperCase()}
                  </p>
                </div>
              </div>

              <button
                onClick={handleLogout}
                className="bg-gradient-to-r from-red-500 to-red-600 text-white px-6 py-2 rounded-full font-semibold hover:from-red-600 hover:to-red-700 transition-all duration-300 transform hover:scale-105"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-6 py-8">
        {/* Stats Overview */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <StatCard
            title="Total Employees"
            value={stats.totalEmployees}
            icon="👥"
            color="bg-blue-100"
            trend={12}
          />
          <StatCard
            title="Active Projects"
            value={stats.activeProjects}
            icon="📊"
            color="bg-green-100"
            trend={8}
          />
          <StatCard
            title="Pending Leaves"
            value={stats.pendingLeaves}
            icon="📅"
            color="bg-yellow-100"
            trend={-5}
          />
          <StatCard
            title="Completed Tasks"
            value={stats.completedTasks}
            icon="✅"
            color="bg-purple-100"
            trend={15}
          />
        </div>

        {/* Navigation Tabs */}
        <div className="bg-white rounded-2xl p-2 shadow-lg mb-8">
          <div className="flex space-x-2 overflow-x-auto">
            <TabButton
              id="overview"
              label="Overview"
              isActive={activeTab === 'overview'}
              onClick={setActiveTab}
            />
            <TabButton
              id="profile"
              label={user.role === 'admin' ? 'User Management' : 'My Profile'}
              isActive={activeTab === 'profile'}
              onClick={setActiveTab}
            />
            <TabButton
              id="analytics"
              label="Analytics"
              isActive={activeTab === 'analytics'}
              onClick={setActiveTab}
            />
            <TabButton
              id="settings"
              label="Settings"
              isActive={activeTab === 'settings'}
              onClick={setActiveTab}
            />
          </div>
        </div>

        {/* Tab Content */}
        <div className="bg-white rounded-2xl shadow-lg overflow-hidden">
          {activeTab === 'overview' && (
            <div className="p-8">
              <h2 className="text-2xl font-bold text-gray-900 mb-6">Dashboard Overview</h2>
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                <div className="bg-gradient-to-r from-blue-50 to-indigo-50 p-6 rounded-2xl">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4">Recent Activity</h3>
                  <div className="space-y-4">
                    <div className="flex items-center space-x-3">
                      <div className="w-2 h-2 bg-blue-600 rounded-full"></div>
                      <p className="text-gray-700">New employee onboarded</p>
                      <span className="text-xs text-gray-500">2 hours ago</span>
                    </div>
                    <div className="flex items-center space-x-3">
                      <div className="w-2 h-2 bg-green-600 rounded-full"></div>
                      <p className="text-gray-700">Leave request approved</p>
                      <span className="text-xs text-gray-500">4 hours ago</span>
                    </div>
                    <div className="flex items-center space-x-3">
                      <div className="w-2 h-2 bg-yellow-600 rounded-full"></div>
                      <p className="text-gray-700">Project milestone completed</p>
                      <span className="text-xs text-gray-500">1 day ago</span>
                    </div>
                  </div>
                </div>

                <div className="bg-gradient-to-r from-purple-50 to-pink-50 p-6 rounded-2xl">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4">Quick Actions</h3>
                  <div className="grid grid-cols-2 gap-4">
                    <button className="bg-white p-4 rounded-xl shadow-sm hover:shadow-md transition-all duration-300 text-center">
                      <span className="text-2xl mb-2 block">📝</span>
                      <span className="text-sm font-medium text-gray-700">New Task</span>
                    </button>
                    <button className="bg-white p-4 rounded-xl shadow-sm hover:shadow-md transition-all duration-300 text-center">
                      <span className="text-2xl mb-2 block">📊</span>
                      <span className="text-sm font-medium text-gray-700">Reports</span>
                    </button>
                    <button className="bg-white p-4 rounded-xl shadow-sm hover:shadow-md transition-all duration-300 text-center">
                      <span className="text-2xl mb-2 block">👥</span>
                      <span className="text-sm font-medium text-gray-700">Team</span>
                    </button>
                    <button className="bg-white p-4 rounded-xl shadow-sm hover:shadow-md transition-all duration-300 text-center">
                      <span className="text-2xl mb-2 block">⚙️</span>
                      <span className="text-sm font-medium text-gray-700">Settings</span>
                    </button>
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'profile' && (
            <div className="p-8">
              {user.role === 'admin' ? <AdminPanel /> : <EmployeeProfile />}
            </div>
          )}

          {activeTab === 'analytics' && (
            <div className="p-8">
              <h2 className="text-2xl font-bold text-gray-900 mb-6">Analytics & Reports</h2>
              <div className="bg-gradient-to-r from-blue-50 to-indigo-50 p-8 rounded-2xl text-center">
                <span className="text-6xl mb-4 block">📈</span>
                <h3 className="text-xl font-semibold text-gray-900 mb-2">Analytics Coming Soon</h3>
                <p className="text-gray-600">Advanced analytics and reporting features will be available soon.</p>
              </div>
            </div>
          )}

          {activeTab === 'settings' && (
            <div className="p-8">
              <h2 className="text-2xl font-bold text-gray-900 mb-6">Settings</h2>
              <div className="bg-gradient-to-r from-purple-50 to-pink-50 p-8 rounded-2xl text-center">
                <span className="text-6xl mb-4 block">⚙️</span>
                <h3 className="text-xl font-semibold text-gray-900 mb-2">Settings Panel</h3>
                <p className="text-gray-600">Customize your dashboard preferences and account settings.</p>
              </div>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default Dashboard;
