import React, { useState, useEffect } from 'react';

function EmployeeDashboard() {
  const [theme, setTheme] = useState('light');
  const [activeTab, setActiveTab] = useState('profile');
  const [employee, setEmployee] = useState(null);
  const [holidays, setHolidays] = useState([]);
  const [leaveRequests, setLeaveRequests] = useState([]);
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
  const [leaveForm, setLeaveForm] = useState({
    leaveType: 'casual',
    startDate: '',
    endDate: '',
    reason: ''
  });

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') || 'light';
    setTheme(savedTheme);
    document.documentElement.setAttribute('data-theme', savedTheme);
    fetchEmployeeProfile();
    fetchHolidays();
    fetchLeaveRequests();
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme);
    document.documentElement.setAttribute('data-theme', newTheme);
  };

  const fetchEmployeeProfile = async () => {
    try {
      const user = JSON.parse(localStorage.getItem('user'));
      console.log('Current user:', user);

      const response = await fetch('http://localhost:5001/api/employees');
      const employees = await response.json();
      console.log('All employees:', employees);

      let currentEmployee = employees.find(emp => 
        emp.workEmail === user.email || 
        emp.personalEmail === user.email ||
        emp.email === user.email
      );

      // If no employee found, create a fallback profile
      if (!currentEmployee) {
        currentEmployee = {
          name: user.name || 'Employee',
          email: user.email,
          employeeId: 'EMP001',
          department: 'General',
          dateOfBirth: null,
          dateOfJoining: new Date(),
          availableLeaves: 20,
          _id: 'temp-id'
        };
      }

      setEmployee({
        ...currentEmployee,
        email: currentEmployee.workEmail || currentEmployee.personalEmail || currentEmployee.email,
        availableLeaves: currentEmployee.availableLeaves || 20
      });
    } catch (error) {
      console.error('Error fetching employee profile:', error);
      // Set fallback employee data
      const user = JSON.parse(localStorage.getItem('user'));
      setEmployee({
        name: user.name || 'Employee',
        email: user.email,
        employeeId: 'EMP001',
        department: 'General',
        dateOfBirth: null,
        dateOfJoining: new Date(),
        availableLeaves: 20,
        _id: 'temp-id'
      });
    }
  };

  const fetchHolidays = async () => {
    try {
      const response = await fetch('http://localhost:5001/api/holidays');
      const data = await response.json();
      setHolidays(data);
    } catch (error) {
      console.error('Error fetching holidays:', error);
    }
  };

  const fetchLeaveRequests = async () => {
    try {
      const response = await fetch('http://localhost:5001/api/leave-requests');
      const data = await response.json();
      const userRequests = employee ? data.filter(req => 
        req.employeeId === employee.employeeId || 
        req.employeeName === employee.name
      ) : [];
      setLeaveRequests(userRequests);
    } catch (error) {
      console.error('Error fetching leave requests:', error);
      setLeaveRequests([]);
    }
  };

  const handleLeaveFormChange = (e) => {
    setLeaveForm({
      ...leaveForm,
      [e.target.name]: e.target.value
    });
  };

  const calculateDays = () => {
    if (leaveForm.startDate && leaveForm.endDate) {
      const start = new Date(leaveForm.startDate);
      const end = new Date(leaveForm.endDate);
      const diffTime = Math.abs(end - start);
      const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)) + 1;
      return diffDays;
    }
    return 0;
  };

  const handleLeaveSubmit = async (e) => {
    e.preventDefault();
    if (!employee) return;

    try {
      const days = calculateDays();
      const response = await fetch('http://localhost:5001/api/employee/leave-requests', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          employeeId: employee._id,
          leaveType: leaveForm.leaveType,
          fromDate: leaveForm.startDate,
          toDate: leaveForm.endDate,
          days,
          reason: leaveForm.reason
        }),
      });

      if (response.ok) {
        setLeaveForm({ leaveType: 'casual', startDate: '', endDate: '', reason: '' });
        fetchLeaveRequests();
        alert('Leave request submitted successfully!');
      }
    } catch (error) {
      console.error('Error submitting leave request:', error);
    }
  };

  const filteredHolidays = holidays.filter(holiday => holiday.year === selectedYear);

  if (!employee) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="text-lg mb-4">Loading employee data...</div>
          <div className="text-sm text-gray-500">If this persists, please contact admin</div>
        </div>
      </div>
    );
  }

  return (
    <div className={`min-h-screen ${theme === 'dark' ? 'bg-gray-900 text-white' : 'bg-gray-50 text-gray-900'}`}>
      <div className="flex">
        <div className={`w-64 ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} shadow-lg h-screen fixed`}>
          <div className="p-6">
            <h2 className="text-xl font-bold text-indigo-600 mb-8">Employee Dashboard</h2>
            <nav className="space-y-2">
              <button
                onClick={() => setActiveTab('profile')}
                className={`w-full text-left px-4 py-2 rounded-lg transition-colors ${
                  activeTab === 'profile'
                    ? 'bg-indigo-600 text-white'
                    : theme === 'dark'
                    ? 'text-gray-300 hover:bg-gray-700'
                    : 'text-gray-600 hover:bg-gray-100'
                }`}
              >
                Personal Details
              </button>
              <button
                onClick={() => setActiveTab('leave')}
                className={`w-full text-left px-4 py-2 rounded-lg transition-colors ${
                  activeTab === 'leave'
                    ? 'bg-indigo-600 text-white'
                    : theme === 'dark'
                    ? 'text-gray-300 hover:bg-gray-700'
                    : 'text-gray-600 hover:bg-gray-100'
                }`}
              >
                Leave Requests
              </button>
              <button
                onClick={() => setActiveTab('holidays')}
                className={`w-full text-left px-4 py-2 rounded-lg transition-colors ${
                  activeTab === 'holidays'
                    ? 'bg-indigo-600 text-white'
                    : theme === 'dark'
                    ? 'text-gray-300 hover:bg-gray-700'
                    : 'text-gray-600 hover:bg-gray-100'
                }`}
              >
                Holiday Calendar
              </button>
            </nav>
          </div>
          <div className="absolute bottom-4 left-4">
            <button
              onClick={toggleTheme}
              className={`p-2 rounded-lg transition-colors ${
                theme === 'dark'
                  ? 'bg-gray-700 text-yellow-400 hover:bg-gray-600'
                  : 'bg-gray-200 text-gray-600 hover:bg-gray-300'
              }`}
            >
              {theme === 'dark' ? '‚òÄÔ∏è' : 'üåô'}
            </button>
          </div>
        </div>

        <div className="ml-64 flex-1 p-8">
          <div className="flex justify-between items-center mb-8">
            <h1 className="text-2xl font-bold">
              {activeTab === 'profile' && 'Personal Details'}
              {activeTab === 'leave' && 'Leave Requests'}
              {activeTab === 'holidays' && 'Holiday Calendar'}
            </h1>
            <button
              onClick={() => {
                localStorage.removeItem('user');
                window.location.href = '/auth';
              }}
              className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
            >
              Logout
            </button>
          </div>

          {activeTab === 'profile' && (
            <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-lg shadow-md`}>
              <h2 className="text-xl font-semibold mb-6">Employee Information</h2>
              <div className="grid grid-cols-2 gap-6">
                <div>
                  <label className="block text-sm font-medium mb-2">Name</label>
                  <p className={`p-3 rounded-lg ${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-100'}`}>
                    {employee.name}
                  </p>
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">Email</label>
                  <p className={`p-3 rounded-lg ${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-100'}`}>
                    {employee.email}
                  </p>
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">Employee ID</label>
                  <p className={`p-3 rounded-lg ${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-100'}`}>
                    {employee.employeeId}
                  </p>
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">Department</label>
                  <p className={`p-3 rounded-lg ${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-100'}`}>
                    {employee.department}
                  </p>
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">Date of Birth</label>
                  <p className={`p-3 rounded-lg ${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-100'}`}>
                    {employee.dateOfBirth ? new Date(employee.dateOfBirth).toLocaleDateString() : 'N/A'}
                  </p>
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">Date of Joining</label>
                  <p className={`p-3 rounded-lg ${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-100'}`}>
                    {employee.dateOfJoining ? new Date(employee.dateOfJoining).toLocaleDateString() : 'N/A'}
                  </p>
                </div>
                <div className="col-span-2">
                  <label className="block text-sm font-medium mb-2">Available Leaves</label>
                  <div className={`p-4 rounded-lg ${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-100'}`}>
                    <span className={`text-2xl font-bold ${
                      employee.availableLeaves > 10
                        ? 'text-green-600'
                        : employee.availableLeaves > 5
                        ? 'text-yellow-600'
                        : 'text-red-600'
                    }`}>
                      {employee.availableLeaves} days
                    </span>
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'leave' && (
            <div>
              <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-lg shadow-md mb-6`}>
                <h3 className="text-lg font-semibold mb-4">Submit Leave Request</h3>
                <div className="mb-4">
                  <span className="text-sm font-medium">Available Leaves: </span>
                  <span className={`font-bold ${
                    employee.availableLeaves > 10
                      ? 'text-green-600'
                      : employee.availableLeaves > 5
                      ? 'text-yellow-600'
                      : 'text-red-600'
                  }`}>
                    {employee.availableLeaves} days
                  </span>
                </div>
                <form onSubmit={handleLeaveSubmit} className="grid grid-cols-2 gap-4">
                  <select
                    name="leaveType"
                    value={leaveForm.leaveType}
                    onChange={handleLeaveFormChange}
                    required
                    className={`p-3 border rounded-lg ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                  >
                    <option value="casual">Casual Leave</option>
                    <option value="sick">Sick Leave</option>
                    <option value="annual">Annual Leave</option>
                    <option value="emergency">Emergency Leave</option>
                  </select>
                  <div></div>
                  <input
                    type="date"
                    name="startDate"
                    value={leaveForm.startDate}
                    onChange={handleLeaveFormChange}
                    required
                    className={`p-3 border rounded-lg ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                  />
                  <input
                    type="date"
                    name="endDate"
                    value={leaveForm.endDate}
                    onChange={handleLeaveFormChange}
                    required
                    className={`p-3 border rounded-lg ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                  />
                  <textarea
                    name="reason"
                    placeholder="Reason for leave"
                    value={leaveForm.reason}
                    onChange={handleLeaveFormChange}
                    required
                    rows="3"
                    className={`col-span-2 p-3 border rounded-lg ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                  />
                  {leaveForm.startDate && leaveForm.endDate && (
                    <div className="col-span-2">
                      <p className="text-sm font-medium">Total Days: {calculateDays()}</p>
                    </div>
                  )}
                  <button
                    type="submit"
                    className="col-span-2 px-6 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition-colors"
                  >
                    Submit Leave Request
                  </button>
                </form>
              </div>

              <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow-md overflow-hidden`}>
                <div className="p-6 border-b">
                  <h3 className="text-lg font-semibold">My Leave Requests</h3>
                </div>
                <table className="w-full">
                  <thead className={`${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-50'}`}>
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Leave Type</th>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Start Date</th>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">End Date</th>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Days</th>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Reason</th>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Status</th>
                    </tr>
                  </thead>
                  <tbody className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} divide-y ${theme === 'dark' ? 'divide-gray-700' : 'divide-gray-200'}`}>
                    {leaveRequests.map((request) => (
                      <tr key={request._id}>
                        <td className="px-6 py-4 whitespace-nowrap">{request.leaveType}</td>
                        <td className="px-6 py-4 whitespace-nowrap">{new Date(request.startDate).toLocaleDateString()}</td>
                        <td className="px-6 py-4 whitespace-nowrap">{new Date(request.endDate).toLocaleDateString()}</td>
                        <td className="px-6 py-4 whitespace-nowrap">{request.days}</td>
                        <td className="px-6 py-4 whitespace-nowrap">{request.reason}</td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`px-2 py-1 text-xs font-semibold rounded-full ${
                            request.status === 'approved'
                              ? 'bg-green-100 text-green-800'
                              : request.status === 'rejected'
                              ? 'bg-red-100 text-red-800'
                              : 'bg-yellow-100 text-yellow-800'
                          }`}>
                            {request.status}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}

          {activeTab === 'holidays' && (
            <div>
              <div className="mb-6">
                <select
                  value={selectedYear}
                  onChange={(e) => setSelectedYear(parseInt(e.target.value))}
                  className={`p-2 border rounded-lg ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                >
                  {[2023, 2024, 2025, 2026].map(year => (
                    <option key={year} value={year}>{year}</option>
                  ))}
                </select>
              </div>

              <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow-md overflow-hidden`}>
                <table className="w-full">
                  <thead className={`${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-50'}`}>
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Holiday Name</th>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Date</th>
                      <th className="px-6 py-3 text-left text-xs font-medium uppercase tracking-wider">Day</th>
                    </tr>
                  </thead>
                  <tbody className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} divide-y ${theme === 'dark' ? 'divide-gray-700' : 'divide-gray-200'}`}>
                    {filteredHolidays.map((holiday) => (
                      <tr key={holiday._id}>
                        <td className="px-6 py-4 whitespace-nowrap font-medium">{holiday.name}</td>
                        <td className="px-6 py-4 whitespace-nowrap">{new Date(holiday.date).toLocaleDateString()}</td>
                        <td className="px-6 py-4 whitespace-nowrap">{new Date(holiday.date).toLocaleDateString('en-US', { weekday: 'long' })}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default EmployeeDashboard;
