import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function LandingPage() {
  const [text, setText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopNum, setLoopNum] = useState(0);
  const [typingSpeed, setTypingSpeed] = useState(150);

  const words = ['Employee Management', 'Team Collaboration', 'HR Solutions', 'Workforce Analytics'];

  useEffect(() => {
    const handleType = () => {
      const current = loopNum % words.length;
      const fullText = words[current];

      setText(isDeleting 
        ? fullText.substring(0, text.length - 1)
        : fullText.substring(0, text.length + 1)
      );

      setTypingSpeed(isDeleting ? 30 : 150);

      if (!isDeleting && text === fullText) {
        setTimeout(() => setIsDeleting(true), 500);
      } else if (isDeleting && text === '') {
        setIsDeleting(false);
        setLoopNum(loopNum + 1);
      }
    };

    const timer = setTimeout(handleType, typingSpeed);
    return () => clearTimeout(timer);
  }, [text, isDeleting, loopNum, typingSpeed, words]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 via-blue-700 to-blue-800 pt-16">
      <div className="container mx-auto px-6 py-12">
        <div className="flex flex-col lg:flex-row items-center justify-between">
          {/* Hero Text */}
          <div className="lg:w-1/2 text-white mb-12 lg:mb-0">
            <h1 className="text-5xl lg:text-6xl font-bold mb-6 leading-tight">
              Modern{' '}
              <span className="bg-gradient-to-r from-blue-200 to-white bg-clip-text text-transparent">
                {text}
              </span>
              <span className="animate-pulse">|</span>
            </h1>
            <p className="text-xl mb-8 text-blue-100 leading-relaxed">
              Streamline your workforce management with our comprehensive employee management system. 
              Track performance, manage leaves, and boost productivity with ease.
            </p>
            <div className="flex flex-col sm:flex-row gap-4">
              <Link 
                to="/auth" 
                className="bg-white text-blue-600 px-8 py-4 rounded-full font-semibold hover:bg-blue-50 transition-all duration-300 transform hover:scale-105 flex items-center justify-center gap-2"
              >
                Get Started
                <svg className="w-5 h-5" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <path d="M5 12h14M12 5l7 7-7 7"/>
                </svg>
              </Link>
              <button className="border-2 border-white text-white px-8 py-4 rounded-full font-semibold hover:bg-white hover:text-blue-600 transition-all duration-300 flex items-center justify-center gap-2">
                Watch Demo
                <svg className="w-5 h-5" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <polygon points="5,3 19,12 5,21"/>
                </svg>
              </button>
            </div>
          </div>

          {/* Animation */}
          <div className="lg:w-1/2 relative flex justify-center">
            <div className="relative w-96 h-96">
              {/* Rotating outer ring */}
              <div className="absolute inset-0 border-4 border-blue-300 rounded-full animate-spin" style={{animationDuration: '20s'}}></div>

              {/* Floating geometric shapes */}
              <div className="absolute top-8 left-8 w-16 h-16 bg-blue-400 transform rotate-45 animate-bounce opacity-70"></div>
              <div className="absolute top-8 right-8 w-12 h-12 bg-blue-500 rounded-full animate-pulse opacity-80"></div>
              <div className="absolute bottom-8 left-8 w-14 h-14 bg-blue-600 animate-bounce opacity-60" style={{animationDelay: '0.5s', clipPath: 'polygon(50% 0%, 0% 100%, 100% 100%)'}}></div>
              <div className="absolute bottom-8 right-8 w-10 h-16 bg-blue-700 animate-pulse opacity-70" style={{animationDelay: '1s'}}></div>

              {/* Central rotating element */}
              <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
                <div className="w-32 h-32 bg-gradient-to-br from-blue-400 to-blue-600 rounded-lg animate-spin" style={{animationDuration: '8s'}}>
                  <div className="flex items-center justify-center h-full text-white text-2xl font-bold">EMS</div>
                </div>
              </div>

              {/* Orbiting dots */}
              <div className="absolute top-1/2 left-1/2 w-48 h-48 transform -translate-x-1/2 -translate-y-1/2">
                <div className="relative w-full h-full animate-spin" style={{animationDuration: '15s'}}>
                  <div className="absolute top-0 left-1/2 w-4 h-4 bg-blue-300 rounded-full transform -translate-x-1/2"></div>
                  <div className="absolute bottom-0 left-1/2 w-4 h-4 bg-blue-400 rounded-full transform -translate-x-1/2"></div>
                  <div className="absolute left-0 top-1/2 w-4 h-4 bg-blue-500 rounded-full transform -translate-y-1/2"></div>
