import React, { useState, useEffect } from 'react';

const LifeOrganizerCalendar = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(null);
  const [isDark, setIsDark] = useState(false);
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [taskType, setTaskType] = useState('reminder');
  const [showTaskModal, setShowTaskModal] = useState(false);

  const monthThemes = {
    0: { bg: 'from-blue-100 to-blue-200', accent: 'bg-blue-500', name: 'January', emoji: '‚ùÑÔ∏è' },
    1: { bg: 'from-pink-100 to-red-200', accent: 'bg-pink-500', name: 'February', emoji: 'üíï' },
    2: { bg: 'from-green-100 to-emerald-200', accent: 'bg-green-500', name: 'March', emoji: 'üå∏' },
    3: { bg: 'from-yellow-100 to-orange-200', accent: 'bg-yellow-500', name: 'April', emoji: 'üå∑' },
    4: { bg: 'from-emerald-100 to-green-200', accent: 'bg-emerald-500', name: 'May', emoji: 'üå∫' },
    5: { bg: 'from-cyan-100 to-blue-200', accent: 'bg-cyan-500', name: 'June', emoji: '‚òÄÔ∏è' },
    6: { bg: 'from-orange-100 to-red-200', accent: 'bg-orange-500', name: 'July', emoji: 'üèñÔ∏è' },
    7: { bg: 'from-yellow-100 to-amber-200', accent: 'bg-amber-500', name: 'August', emoji: 'üåª' },
    8: { bg: 'from-amber-100 to-orange-200', accent: 'bg-amber-600', name: 'September', emoji: 'üçÇ' },
    9: { bg: 'from-orange-100 to-red-200', accent: 'bg-orange-600', name: 'October', emoji: 'üéÉ' },
    10: { bg: 'from-gray-100 to-slate-200', accent: 'bg-gray-500', name: 'November', emoji: 'ü¶É' },
    11: { bg: 'from-red-100 to-green-200', accent: 'bg-red-500', name: 'December', emoji: 'üéÑ' }
  };

  const taskIcons = {
    birthday: 'üéÇ',
    holiday: 'üéâ',
    reminder: '‚è∞',
    deadline: 'üìå',
    meeting: 'üë•',
    personal: 'üí≠'
  };

  const taskColors = {
    urgent: 'bg-red-500 border-red-600',
    normal: 'bg-blue-400 border-blue-500',
    holiday: 'bg-green-500 border-green-600',
    birthday: 'bg-pink-500 border-pink-600'
  };

  const quotes = [
    "Today is a new beginning. Make it count! ‚ú®",
    "Progress, not perfection. You've got this! üí™",
    "Small steps lead to big changes. Keep going! üöÄ",
    "Your future self will thank you for today's efforts! üåü"
  ];

  const currentTheme = monthThemes[currentDate.getMonth()];
  const todayQuote = quotes[new Date().getDate() % quotes.length];

  const getDaysInMonth = (date) => {
    return new Date(date.getFullYear(), date.getMonth() + 1, 0).getDate();
  };

  const getFirstDayOfMonth = (date) => {
    return new Date(date.getFullYear(), date.getMonth(), 1).getDay();
  };

  const navigateMonth = (direction) => {
    const newDate = new Date(currentDate);
    newDate.setMonth(currentDate.getMonth() + direction);
    setCurrentDate(newDate);
  };

  const addTask = () => {
    if (!newTask.trim() || !selectedDate) return;

    const task = {
      id: Date.now(),
      text: newTask,
      type: taskType,
      date: selectedDate,
      priority: taskType === 'deadline' ? 'urgent' : taskType === 'holiday' ? 'holiday' : 'normal'
    };

    setTasks([...tasks, task]);
    setNewTask('');
    setShowTaskModal(false);
  };

  const getTasksForDate = (date) => {
    return tasks.filter(task => 
      new Date(task.date).toDateString() === date.toDateString()
    );
  };

  const getUpcomingTasks = () => {
    const today = new Date();
    return tasks
      .filter(task => new Date(task.date) >= today)
      .sort((a, b) => new Date(a.date) - new Date(b.date))
      .slice(0, 5);
  };

  const renderCalendarDays = () => {
    const daysInMonth = getDaysInMonth(currentDate);
    const firstDay = getFirstDayOfMonth(currentDate);
    const days = [];
    const today = new Date();

    // Empty cells for days before month starts
    for (let i = 0; i < firstDay; i++) {
      days.push(<div key={`empty-${i}`} className="h-20"></div>);
    }

    // Days of the month
    for (let day = 1; day <= daysInMonth; day++) {
      const date = new Date(currentDate.getFullYear(), currentDate.getMonth(), day);
      const dayTasks = getTasksForDate(date);
      const isToday = date.toDateString() === today.toDateString();
      const isSelected = selectedDate && date.toDateString() === selectedDate.toDateString();

      days.push(
        <div
          key={day}
          onClick={() => {
            setSelectedDate(date);
            if (dayTasks.length === 0) setShowTaskModal(true);
          }}
          className={`h-20 p-2 rounded-xl cursor-pointer transition-all duration-300 hover:scale-105 hover:shadow-lg ${
            isToday ? `${currentTheme.accent} text-white shadow-lg` :
            isSelected ? 'bg-white shadow-md border-2 border-blue-400' :
            isDark ? 'bg-gray-800 hover:bg-gray-700' : 'bg-white/50 hover:bg-white/80'
          }`}
        >
          <div className="font-semibold text-sm">{day}</div>
          <div className="space-y-1 mt-1">
            {dayTasks.slice(0, 2).map(task => (
              <div
                key={task.id}
                className={`text-xs px-2 py-1 rounded-full text-white ${taskColors[task.priority]} truncate`}
              >
                {taskIcons[task.type]} {task.text}
              </div>
            ))}
            {dayTasks.length > 2 && (
              <div className="text-xs text-gray-500">+{dayTasks.length - 2} more</div>
            )}
          </div>
        </div>
      );
    }

    return days;
  };

  return (
    <div className={`min-h-screen transition-all duration-500 ${isDark ? 'bg-gray-900 text-white' : `bg-gradient-to-br ${currentTheme.bg}`}`}>
      <div className="flex h-screen">
        {/* Sidebar */}
        <div className={`w-80 p-6 ${isDark ? 'bg-gray-800' : 'bg-white/30 backdrop-blur-md'} border-r`}>
          <div className="space-y-6">
            {/* Header */}
            <div className="flex items-center justify-between">
              <h1 className="text-2xl font-bold">Life Organizer</h1>
              <button
                onClick={() => setIsDark(!isDark)}
                className={`p-2 rounded-full transition-all duration-300 ${isDark ? 'bg-yellow-400 text-gray-900' : 'bg-gray-800 text-white'}`}
              >
                {isDark ? '‚òÄÔ∏è' : 'üåô'}
              </button>
            </div>

            {/* Quote of the day */}
            <div className={`p-4 rounded-xl ${isDark ? 'bg-gray-700' : 'bg-white/50'}`}>
              <h3 className="font-semibold mb-2">Daily Inspiration</h3>
              <p className="text-sm italic">{todayQuote}</p>
            </div>

            {/* Quick add task */}
            <div className={`p-4 rounded-xl ${isDark ? 'bg-gray-700' : 'bg-white/50'}`}>
              <h3 className="font-semibold mb-3">Quick Add</h3>
              <div className="space-y-2">
                <input
                  type="text"
                  placeholder="What's on your mind?"
                  value={newTask}
                  onChange={(e) => setNewTask(e.target.value)}
                  className={`w-full p-2 rounded-lg border ${isDark ? 'bg-gray-600 border-gray-500' : 'bg-white border-gray-300'}`}
                />
                <select
                  value={taskType}
                  onChange={(e) => setTaskType(e.target.value)}
                  className={`w-full p-2 rounded-lg border ${isDark ? 'bg-gray-600 border-gray-500' : 'bg-white border-gray-300'}`}
                >
                  <option value="reminder">‚è∞ Reminder</option>
                  <option value="deadline">üìå Deadline</option>
                  <option value="birthday">üéÇ Birthday</option>
                  <option value="holiday">üéâ Holiday</option>
                  <option value="meeting">üë• Meeting</option>
                  <option value="personal">üí≠ Personal</option>
                </select>
                <button
                  onClick={addTask}
                  disabled={!newTask.trim() || !selectedDate}
                  className={`w-full py-2 rounded-lg font-semibold transition-all ${
                    !newTask.trim() || !selectedDate
                      ? 'bg-gray-400 cursor-not-allowed'
                      : `${currentTheme.accent} text-white hover:opacity-90`
                  }`}
                >
                  Add Task
                </button>
              </div>
            </div>

            {/* Upcoming tasks */}
            <div className={`p-4 rounded-xl ${isDark ? 'bg-gray-700' : 'bg-white/50'}`}>
              <h3 className="font-semibold mb-3">Upcoming</h3>
              <div className="space-y-2 max-h-60 overflow-y-auto">
                {getUpcomingTasks().map(task => (
                  <div key={task.id} className={`p-2 rounded-lg ${taskColors[task.priority]} text-white text-sm`}>
                    <div className="flex items-center gap-2">
                      <span>{taskIcons[task.type]}</span>
                      <span className="flex-1 truncate">{task.text}</span>
                    </div>
                    <div className="text-xs opacity-75 mt-1">
                      {new Date(task.date).toLocaleDateString()}
                    </div>
                  </div>
                ))}
                {getUpcomingTasks().length === 0 && (
                  <p className="text-gray-500 text-sm">No upcoming tasks</p>
                )}
              </div>
            </div>
          </div>
        </div>

        {/* Main Calendar */}
        <div className="flex-1 p-8">
          {/* Calendar Header */}
          <div className="flex items-center justify-between mb-8">
            <div className="flex items-center gap-4">
              <button
                onClick={() => navigateMonth(-1)}
                className={`p-3 rounded-full ${currentTheme.accent} text-white hover:opacity-90 transition-all`}
              >
                ‚Üê
              </button>
              <h2 className="text-4xl font-bold flex items-center gap-3">
                {currentTheme.emoji} {currentTheme.name} {currentDate.getFullYear()}
              </h2>
              <button
                onClick={() => navigateMonth(1)}
                className={`p-3 rounded-full ${currentTheme.accent} text-white hover:opacity-90 transition-all`}
              >
                ‚Üí
              </button>
            </div>
          </div>

          {/* Calendar Grid */}
          <div className="grid grid-cols-7 gap-4 mb-4">
            {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
              <div key={day} className="text-center font-semibold py-2">
                {day}
              </div>
            ))}
          </div>

          <div className="grid grid-cols-7 gap-4">
            {renderCalendarDays()}
          </div>
        </div>
      </div>

      {/* Task Modal */}
      {showTaskModal && selectedDate && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
          <div className={`p-6 rounded-2xl max-w-md w-full mx-4 ${isDark ? 'bg-gray-800' : 'bg-white'}`}>
            <h3 className="text-xl font-bold mb-4">
              Add task for {selectedDate.toLocaleDateString()}
            </h3>
            <div className="space-y-4">
              <input
                type="text"
                placeholder="Task description"
                value={newTask}
                onChange={(e) => setNewTask(e.target.value)}
                className={`w-full p-3 rounded-lg border ${isDark ? 'bg-gray-700 border-gray-600' : 'bg-gray-50 border-gray-300'}`}
              />
              <select
                value={taskType}
                onChange={(e) => setTaskType(e.target.value)}
                className={`w-full p-3 rounded-lg border ${isDark ? 'bg-gray-700 border-gray-600' : 'bg-gray-50 border-gray-300'}`}
              >
                <option value="reminder">‚è∞ Reminder</option>
                <option value="deadline">üìå Deadline</option>
                <option value="birthday">üéÇ Birthday</option>
                <option value="holiday">üéâ Holiday</option>
                <option value="meeting">üë• Meeting</option>
                <option value="personal">üí≠ Personal</option>
              </select>
              <div className="flex gap-3">
                <button
                  onClick={addTask}
                  className={`flex-1 py-3 rounded-lg font-semibold ${currentTheme.accent} text-white hover:opacity-90`}
                >
                  Add Task
                </button>
                <button
                  onClick={() => setShowTaskModal(false)}
                  className={`flex-1 py-3 rounded-lg font-semibold ${isDark ? 'bg-gray-600 hover:bg-gray-500' : 'bg-gray-300 hover:bg-gray-400'}`}
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default LifeOrganizerCalendar;
