import React, { useState, useEffect } from 'react';

function Payroll({ theme, employees: propEmployees }) {
  const [employees, setEmployees] = useState(propEmployees || []);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [payrollData, setPayrollData] = useState({
    basicSalary: 0,
    hra: 0,
    da: 0,
    bonus: 0,
    overtime: 0,
    leaveDeductions: 0,
    taxDeduction: 0,
    netSalary: 0
  });
  const [showPayslip, setShowPayslip] = useState(false);
  const [currentMonth, setCurrentMonth] = useState(new Date().getMonth());
  const [currentYear, setCurrentYear] = useState(new Date().getFullYear());
  const [searchQuery, setSearchQuery] = useState('');
  const [departmentFilter, setDepartmentFilter] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [employeesPerPage] = useState(8);

  useEffect(() => {
    if (propEmployees && propEmployees.length > 0) {
      setEmployees(propEmployees);
    } else {
      fetchEmployees();
    }
  }, [propEmployees]);

  useEffect(() => {
    if (selectedEmployee) {
      const calculated = calculatePayroll(selectedEmployee);
      setPayrollData(calculated);
    }
  }, [selectedEmployee]);

  const fetchEmployees = async () => {
    try {
      const response = await fetch('http://localhost:5002/api/employees', {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
      });
      const data = await response.json();
      console.log('Fetched employees:', data);
      setEmployees(data);
    } catch (error) {
      console.error('Error fetching employees:', error);
    }
  };

  const calculatePayroll = (employee) => {
    if (!employee.salary) return null;
    const basicSalary = employee.salary;
    const grossSalary = basicSalary;

    // Leave deductions (₹500 per unpaid leave)
    const unpaidLeaves = Math.max(0, (employee.totalLeavesTaken || 0) - (employee.availableLeaves || 20));
    const leaveDeductions = unpaidLeaves * 500;

    // Tax calculation (simplified)
    let taxDeduction = 0;
    const annualSalary = grossSalary * 12;
    if (annualSalary > 1000000) taxDeduction = grossSalary * 0.3;
    else if (annualSalary > 500000) taxDeduction = grossSalary * 0.2;
    else if (annualSalary > 250000) taxDeduction = grossSalary * 0.05;

    const pfDeduction = basicSalary * 0.12;
    const esiDeduction = grossSalary * 0.0175;
    const netSalary = grossSalary + (payrollData.bonus || 0) + (payrollData.overtime || 0) - leaveDeductions - taxDeduction - pfDeduction - esiDeduction;

    return {
      basicSalary,
      grossSalary,
      bonus: payrollData.bonus || 0,
      overtime: payrollData.overtime || 0,
      leaveDeductions,
      taxDeduction,
      netSalary
    };
  };

  const handleEmployeeSelect = (employee) => {
    console.log('Selected employee:', employee);
    console.log('Employee salary:', employee.salary);
    setSelectedEmployee(employee);
    const calculated = calculatePayroll(employee);
    setPayrollData(calculated);
  };

  const generatePayslip = (employee = selectedEmployee, payrollCalc = null) => {
    if (!employee) return;

    const payslipData = payrollCalc || calculatePayroll(employee);
    if (!payslipData) {
      alert('Employee salary not configured');
      return;
    }

    // const payslipData = calculatePayroll(selectedEmployee); // Removed duplicate declaration
    const monthNames = ["January", "February", "March", "April", "May", "June",
      "July", "August", "September", "October", "November", "December"];

    const payslipContent = `
      <div style="font-family: Arial, sans-serif; max-width: 800px; margin: 0 auto; padding: 20px;">
        <div style="text-align: center; border-bottom: 2px solid #333; padding-bottom: 20px; margin-bottom: 20px;">
          <h1 style="color: #333; margin: 0;">CzarCore Technologies</h1>
          <h2 style="color: #666; margin: 10px 0;">SALARY SLIP</h2>
          <p style="margin: 5px 0;">For the month of ${monthNames[currentMonth]} ${currentYear}</p>
        </div>

        <div style="display: flex; justify-content: space-between; margin-bottom: 30px;">
          <div>
            <h3 style="color: #333; border-bottom: 1px solid #ccc; padding-bottom: 5px;">Employee Details</h3>
            <p><strong>Name:</strong> ${employee.name}</p>
            <p><strong>Employee ID:</strong> ${employee.employeeId}</p>
            <p><strong>Department:</strong> ${employee.department}</p>
            <p><strong>Position:</strong> ${employee.position}</p>
          </div>
          <div>
            <h3 style="color: #333; border-bottom: 1px solid #ccc; padding-bottom: 5px;">Pay Period</h3>
            <p><strong>Month:</strong> ${monthNames[currentMonth]} ${currentYear}</p>
            <p><strong>Working Days:</strong> 22</p>
            <p><strong>Paid Days:</strong> ${22 - (employee.totalLeavesTaken || 0)}</p>
          </div>
        </div>

        <table style="width: 100%; border-collapse: collapse; margin-bottom: 20px;">
          <tr style="background-color: #f5f5f5;">
            <th style="border: 1px solid #ddd; padding: 12px; text-align: left;">EARNINGS</th>
            <th style="border: 1px solid #ddd; padding: 12px; text-align: right;">AMOUNT (₹)</th>
            <th style="border: 1px solid #ddd; padding: 12px; text-align: left;">DEDUCTIONS</th>
            <th style="border: 1px solid #ddd; padding: 12px; text-align: right;">AMOUNT (₹)</th>
          </tr>
          <tr>
            <td style="border: 1px solid #ddd; padding: 8px;">Basic Salary</td>
            <td style="border: 1px solid #ddd; padding: 8px; text-align: right;">₹${payslipData.basicSalary.toLocaleString()}</td>
            <td style="border: 1px solid #ddd; padding: 8px;">Income Tax</td>
            <td style="border: 1px solid #ddd; padding: 8px; text-align: right;">₹${payslipData.taxDeduction.toLocaleString()}</td>
          </tr>
          <tr>

            <td style="border: 1px solid #ddd; padding: 8px;">Leave Deductions</td>
            <td style="border: 1px solid #ddd; padding: 8px; text-align: right;">₹${payslipData.leaveDeductions.toLocaleString()}</td>
          </tr>
          <tr>

            <td style="border: 1px solid #ddd; padding: 8px;">PF (12%)</td>
            <td style="border: 1px solid #ddd; padding: 8px; text-align: right;">₹${(payslipData.basicSalary * 0.12).toLocaleString()}</td>
          </tr>
          <tr>
            <td style="border: 1px solid #ddd; padding: 8px;">Bonus</td>
            <td style="border: 1px solid #ddd; padding: 8px; text-align: right;">₹${payslipData.bonus.toLocaleString()}</td>
            <td style="border: 1px solid #ddd; padding: 8px;">ESI (1.75%)</td>
            <td style="border: 1px solid #ddd; padding: 8px; text-align: right;">₹${(payslipData.grossSalary * 0.0175).toLocaleString()}</td>
          </tr>
          <tr>
            <td style="border: 1px solid #ddd; padding: 8px;">Overtime</td>
            <td style="border: 1px solid #ddd; padding: 8px; text-align: right;">₹${payslipData.overtime.toLocaleString()}</td>
            <td style="border: 1px solid #ddd; padding: 8px;"></td>
            <td style="border: 1px solid #ddd; padding: 8px; text-align: right;"></td>
          </tr>
          <tr style="background-color: #f9f9f9; font-weight: bold;">
            <td style="border: 1px solid #ddd; padding: 12px;">GROSS EARNINGS</td>
            <td style="border: 1px solid #ddd; padding: 12px; text-align: right;">₹${(payslipData.grossSalary + payslipData.bonus + payslipData.overtime).toLocaleString()}</td>
            <td style="border: 1px solid #ddd; padding: 12px;">TOTAL DEDUCTIONS</td>
            <td style="border: 1px solid #ddd; padding: 12px; text-align: right;">₹${(payslipData.taxDeduction + payslipData.leaveDeductions + payslipData.basicSalary * 0.12 + payslipData.grossSalary * 0.0175).toLocaleString()}</td>
          </tr>
        </table>

        <div style="background-color: #e8f5e8; padding: 15px; border-radius: 5px; text-align: center;">
          <h2 style="color: #2d5a2d; margin: 0;">NET SALARY: ₹${(payslipData.netSalary - payslipData.basicSalary * 0.12 - payslipData.grossSalary * 0.0175).toLocaleString()}</h2>
        </div>

        <div style="margin-top: 30px; text-align: center; font-size: 12px; color: #666;">
          <p>This is a computer-generated payslip and does not require a signature.</p>
          <p>Generated on: ${new Date().toLocaleDateString()}</p>
        </div>
      </div>
    `;

    const newWindow = window.open('', '_blank');
    newWindow.document.write(payslipContent);
    newWindow.document.close();
    newWindow.print();
  };

  return (
    <div className="space-y-6">
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <h2 className="text-2xl font-bold">Payroll Management</h2>
        <div className="flex flex-wrap gap-2">
          <select
            value={currentMonth}
            onChange={(e) => setCurrentMonth(parseInt(e.target.value))}
            className={`px-3 py-2 rounded-lg border ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
          >
            {Array.from({length: 12}, (_, i) => (
              <option key={i} value={i}>{new Date(0, i).toLocaleString('default', {month: 'long'})}</option>
            ))}
          </select>
          <select
            value={currentYear}
            onChange={(e) => setCurrentYear(parseInt(e.target.value))}
            className={`px-3 py-2 rounded-lg border ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
          >
            {Array.from({length: 5}, (_, i) => (
              <option key={i} value={new Date().getFullYear() - 2 + i}>{new Date().getFullYear() - 2 + i}</option>
            ))}
          </select>
          <button
            onClick={() => {
              const employeesWithSalary = employees.filter(emp => emp.salary);
              if (employeesWithSalary.length === 0) {
                alert('No employees with salary configured');
                return;
              }
              const csvContent = 'Employee ID,Name,Department,Basic Salary,Gross Salary,Tax,Net Salary\n' +
                employeesWithSalary.map(emp => {
                  const calc = calculatePayroll(emp);
                  if (!calc) return '';
                  return `${emp.employeeId},${emp.name},${emp.department},${calc.basicSalary},${calc.grossSalary},${calc.taxDeduction},${calc.netSalary - calc.basicSalary * 0.12 - calc.grossSalary * 0.0175}`;
                }).filter(Boolean).join('\n');
              const blob = new Blob([csvContent], { type: 'text/csv' });
              const url = window.URL.createObjectURL(blob);
              const a = document.createElement('a');
              a.href = url;
              a.download = `payroll-${new Date(0, currentMonth).toLocaleString('default', {month: 'long'})}-${currentYear}.csv`;
              a.click();
            }}
            className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
          >
            Export CSV
          </button>
        </div>
      </div>

      {/* Bulk Actions */}
      <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-4 rounded-lg shadow-md`}>
        <div className="flex flex-wrap gap-3">
          <button
            onClick={() => {
              const employeesWithSalary = employees.filter(emp => emp.salary);
              employeesWithSalary.forEach(emp => {
                const calc = calculatePayroll(emp);
                if (calc) generatePayslip(emp, calc);
              });
            }}
            className="px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition-colors"
          >
            Generate All Payslips
          </button>
          <div className="flex items-center gap-2">
            <span className="text-sm text-gray-500">Bulk Bonus:</span>
            <input
              type="number"
              placeholder="Amount"
              className={`w-24 px-2 py-1 border rounded ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
              onKeyPress={(e) => {
                if (e.key === 'Enter') {
                  const amount = parseFloat(e.target.value) || 0;
                  employees.forEach(emp => {
                    if (emp.salary) {
                      setPayrollData(prev => ({...prev, bonus: amount}));
                    }
                  });
                  e.target.value = '';
                }
              }}
            />
            <span className="text-xs text-gray-400">Press Enter</span>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Employee Selection */}
        <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-lg shadow-md`}>
          <h3 className="text-lg font-semibold mb-4">Select Employee</h3>

          {/* Search and Filter */}
          <div className="mb-4 space-y-3">
            <input
              type="text"
              placeholder="Search by name or employee ID"
              value={searchQuery}
              onChange={(e) => {
                setSearchQuery(e.target.value);
                setCurrentPage(1);
              }}
              className={`w-full p-2 border rounded-lg ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
            />
            <select
              value={departmentFilter}
              onChange={(e) => {
                setDepartmentFilter(e.target.value);
                setCurrentPage(1);
              }}
              className={`w-full p-2 border rounded-lg ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
            >
              <option value="">All Departments</option>
              {[...new Set(employees.map(emp => emp.department).filter(Boolean))].map(dept => (
                <option key={dept} value={dept}>{dept}</option>
              ))}
            </select>
          </div>

          <div className="space-y-2 max-h-80 overflow-y-auto">
            {(() => {
              const filtered = employees.filter(emp => {
                const matchesSearch = !searchQuery || 
                  emp.name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
                  emp.employeeId?.toLowerCase().includes(searchQuery.toLowerCase());
                const matchesDept = !departmentFilter || emp.department === departmentFilter;
                return matchesSearch && matchesDept;
              });

              const startIndex = (currentPage - 1) * employeesPerPage;
              const currentEmployees = filtered.slice(startIndex, startIndex + employeesPerPage);
              const totalPages = Math.ceil(filtered.length / employeesPerPage);

              return (
                <>
                  {currentEmployees.map(employee => (
                    <div
                      key={employee._id}
                      onClick={() => handleEmployeeSelect(employee)}
                      className={`p-3 rounded-lg cursor-pointer transition-colors ${
                        selectedEmployee?._id === employee._id
                          ? 'bg-blue-100 border-blue-300'
                          : theme === 'dark' ? 'bg-gray-700 hover:bg-gray-600' : 'bg-gray-50 hover:bg-gray-100'
                      }`}
                    >
                      <div className="flex justify-between items-center">
                        <div>
                          <p className="font-medium">{employee.name}</p>
                          <p className="text-sm text-gray-500">{employee.employeeId} - {employee.department}</p>
                        </div>
                        <p className="text-sm font-medium">₹{employee.salary ? employee.salary.toLocaleString() : 'Not Set'}</p>
                      </div>
                    </div>
                  ))}

                  {/* Pagination */}
                  {totalPages > 1 && (
                    <div className="flex justify-center gap-2 mt-4 pt-3 border-t">
                      <button
                        onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                        disabled={currentPage === 1}
                        className={`px-3 py-1 rounded text-sm ${currentPage === 1 ? 'bg-gray-300 cursor-not-allowed' : 'bg-blue-600 text-white hover:bg-blue-700'}`}
                      >
                        Prev
                      </button>
                      <span className="px-3 py-1 bg-gray-100 rounded text-sm">
                        {currentPage} of {totalPages}
                      </span>
                      <button
                        onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                        disabled={currentPage === totalPages}
                        className={`px-3 py-1 rounded text-sm ${currentPage === totalPages ? 'bg-gray-300 cursor-not-allowed' : 'bg-blue-600 text-white hover:bg-blue-700'}`}
                      >
                        Next
                      </button>
                    </div>
                  )}

                  {filtered.length === 0 && (
                    <div className="text-center py-4 text-gray-500">
                      No employees found
                    </div>
                  )}
                </>
              );
            })()}
          </div>
        </div>

        {/* Payroll Calculation */}
        {selectedEmployee && (
          <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-lg shadow-md`}>
            <h3 className="text-lg font-semibold mb-4">Payroll Details</h3>

            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-1">Bonus (₹)</label>
                  <input
                    type="number"
                    value={payrollData.bonus}
                    onChange={(e) => setPayrollData({...payrollData, bonus: parseFloat(e.target.value) || 0})}
                    className={`w-full px-3 py-2 rounded-lg border ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium mb-1">Overtime (₹)</label>
                  <input
                    type="number"
                    value={payrollData.overtime}
                    onChange={(e) => setPayrollData({...payrollData, overtime: parseFloat(e.target.value) || 0})}
                    className={`w-full px-3 py-2 rounded-lg border ${theme === 'dark' ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
                  />
                </div>
              </div>

              <div className="border-t pt-4">
                <h4 className="font-medium mb-3">Salary Breakdown</h4>
                <div className="space-y-2 text-sm">
                  <div className="flex justify-between">
                    <span>Basic Salary:</span>
                    <span>₹{payrollData.basicSalary.toLocaleString()}</span>
                  </div>

                  <div className="flex justify-between">
                    <span>Bonus:</span>
                    <span>₹{payrollData.bonus.toLocaleString()}</span>
                  </div>
                  <div className="flex justify-between">
                    <span>Overtime:</span>
                    <span>₹{payrollData.overtime.toLocaleString()}</span>
                  </div>
                  <div className="flex justify-between text-red-600">
                    <span>Leave Deductions:</span>
                    <span>-₹{payrollData.leaveDeductions.toLocaleString()}</span>
                  </div>
                  <div className="flex justify-between text-red-600">
                    <span>Tax Deduction:</span>
                    <span>-₹{payrollData.taxDeduction.toLocaleString()}</span>
                  </div>
                  <div className="flex justify-between text-red-600">
                    <span>PF (12%):</span>
                    <span>-₹{(payrollData.basicSalary * 0.12).toLocaleString()}</span>
                  </div>
                  <div className="flex justify-between text-red-600">
                    <span>ESI (1.75%):</span>
                    <span>-₹{(payrollData.grossSalary * 0.0175).toLocaleString()}</span>
                  </div>
                  <div className="border-t pt-2 flex justify-between font-bold text-lg text-green-600">
                    <span>Net Salary:</span>
                    <span>₹{payrollData.netSalary.toLocaleString()}</span>
                  </div>
                </div>
              </div>

              <button
                onClick={generatePayslip}
                className="w-full px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
              >
                Generate Payslip
              </button>
            </div>
          </div>
        )}
      </div>

      {/* Payroll Summary */}
      <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-6 rounded-lg shadow-md`}>
        <h3 className="text-lg font-semibold mb-4">Monthly Payroll Summary</h3>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div className="text-center">
            <p className="text-2xl font-bold text-blue-600">₹{employees.filter(emp => emp.salary).reduce((sum, emp) => sum + emp.salary, 0).toLocaleString()}</p>
            <p className="text-sm text-gray-500">Total Basic Salary</p>
          </div>
          <div className="text-center">
            <p className="text-2xl font-bold text-green-600">₹{employees.filter(emp => emp.salary).reduce((sum, emp) => sum + emp.salary, 0).toLocaleString()}</p>
            <p className="text-sm text-gray-500">Gross Payroll</p>
          </div>
          <div className="text-center">
            <p className="text-2xl font-bold text-red-600">₹{(employees.filter(emp => emp.salary).reduce((sum, emp) => sum + emp.salary, 0) * 0.25).toLocaleString()}</p>
            <p className="text-sm text-gray-500">Total Deductions</p>
          </div>
          <div className="text-center">
            <p className="text-2xl font-bold text-purple-600">₹{(employees.filter(emp => emp.salary).reduce((sum, emp) => sum + emp.salary, 0) * 0.75).toLocaleString()}</p>
            <p className="text-sm text-gray-500">Net Payroll</p>
          </div>
        </div>
        <div className="mt-4 text-center text-sm text-gray-500">
          {employees.filter(emp => emp.salary).length} of {employees.length} employees have salary configured
        </div>
      </div>
    </div>
  );
}

export default Payroll;
