import React, { useState } from 'react';
import './App.css';

function App() {
  const [isSignUp, setIsSignUp] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: ''
  });
  const [message, setMessage] = useState('');

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const endpoint = isSignUp ? '/api/register' : '/api/login';
      const response = await fetch(`http://localhost:5001${endpoint}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        setMessage(data.message);
        localStorage.setItem('token', data.token);
        localStorage.setItem('user', JSON.stringify(data.user));

        // Redirect based on role
        if (data.user.role === 'admin') {
          window.location.href = '/dashboard';
        } else {
          window.location.href = '/dashboard';
        }
      } else {
        setMessage(data.message);
      }
    } catch (error) {
      setMessage('Network error. Please try again.');
    }
  };

  return (
    <div className="app">
      <div className="container">
        <div className={`form-container ${isSignUp ? 'sign-up-mode' : ''}`}>
          <div className="forms-wrap">
            <form onSubmit={handleSubmit} className={`sign-in-form ${!isSignUp ? 'active' : ''}`}>
              <div className="logo">
                <h4>CzarCore</h4>
              </div>
              <div className="heading">
                <h2>Welcome Back</h2>
                <h6>Not registered yet?</h6>
                <button type="button" className="toggle" onClick={() => setIsSignUp(true)}>
                  Sign up
                </button>
              </div>
              <div className="actual-form">
                <div className="input-wrap">
                  <input
                    type="email"
                    name="email"
                    className="input-field"
                    autoComplete="off"
                    required
                    value={formData.email}
                    onChange={handleInputChange}
                  />
                  <label>Email</label>
                </div>
                <div className="input-wrap">
                  <input
                    type="password"
                    name="password"
                    className="input-field"
                    autoComplete="off"
                    required
                    value={formData.password}
                    onChange={handleInputChange}
                  />
                  <label>Password</label>
                </div>
                <input type="submit" value="Sign In" className="sign-btn" />
                <p className="text">
                  Forgotten your password or your login details?
                  <a href="#"> Get help</a> signing in
                </p>
              </div>
            </form>

            <form onSubmit={handleSubmit} className={`sign-up-form ${isSignUp ? 'active' : ''}`}>
              <div className="logo">
                <h4>CzarCore</h4>
              </div>
              <div className="heading">
                <h2>Get Started</h2>
                <h6>Already have an account?</h6>
                <button type="button" className="toggle" onClick={() => setIsSignUp(false)}>
                  Sign in
                </button>
              </div>
              <div className="actual-form">
                <div className="input-wrap">
                  <input
                    type="text"
                    name="name"
                    className="input-field"
                    autoComplete="off"
                    required
                    value={formData.name}
                    onChange={handleInputChange}
                  />
                  <label>Name</label>
                </div>
                <div className="input-wrap">
                  <input
                    type="email"
                    name="email"
                    className="input-field"
                    autoComplete="off"
                    required
                    value={formData.email}
                    onChange={handleInputChange}
                  />
                  <label>Email</label>
                </div>
                <div className="input-wrap">
                  <input
                    type="password"
                    name="password"
                    className="input-field"
                    autoComplete="off"
                    required
                    value={formData.password}
                    onChange={handleInputChange}
                  />
                  <label>Password</label>
                </div>
                <input type="submit" value="Sign Up" className="sign-btn" />
                <p className="text">
                  By signing up, I agree to the
                  <a href="#"> Terms of Services</a> and
                  <a href="#"> Privacy Policy</a>
                </p>
              </div>
            </form>
          </div>

          <div className="carousel">
            <div className="images-wrapper">
              <img src="/api/placeholder/400/300" className={`image img-1 ${!isSignUp ? 'show' : ''}`} alt="" />
              <img src="/api/placeholder/400/300" className={`image img-2 ${isSignUp ? 'show' : ''}`} alt="" />
            </div>
            <div className="text-slider">
              <div className="text-wrap">
                <div className={`text-group ${!isSignUp ? 'active' : ''}`}>
                  <h2>Create your own courses</h2>
                  <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Facere, nobis?</p>
                </div>
                <div className={`text-group ${isSignUp ? 'active' : ''}`}>
                  <h2>Customize as you like</h2>
                  <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Facere, nobis?</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      {message && (
        <div className="message">
          {message}
        </div>
      )}
    </div>
  );
}

export default App;
