import React, { useState, useEffect } from 'react';
import ProfessionalCalendar from './ProfessionalCalendar';

function AdminPanel() {
  const [theme, setTheme] = useState('light');
  const [activeTab, setActiveTab] = useState('dashboard');
  const [holidays, setHolidays] = useState([]);

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') || 'light';
    setTheme(savedTheme);
    document.documentElement.setAttribute('data-theme', savedTheme);
    fetchHolidays();
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme);
    document.documentElement.setAttribute('data-theme', newTheme);
  };

  const fetchHolidays = async () => {
    try {
      const response = await fetch('http://localhost:5002/api/holidays');
      const data = await response.json();
      setHolidays(data);
    } catch (error) {
      console.error('Error fetching holidays:', error);
    }
  };

  const handleHolidayDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this holiday?')) {
      try {
        const response = await fetch(`http://localhost:5002/api/holidays/${id}`, {
          method: 'DELETE'
        });

        if (response.ok) {
          fetchHolidays();
        } else {
          console.log('Delete failed:', response.status);
        }
      } catch (error) {
        console.error('Error deleting holiday:', error);
      }
    }
  };

  const holidaysArray = Array.isArray(holidays) ? holidays : [];

  return (
    <div className={`min-h-screen ${theme === 'dark' ? 'bg-gray-900 text-white' : 'bg-gray-50 text-gray-900'}`}>
      <div className="flex">
        <div className={`w-64 ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} shadow-lg h-screen fixed z-10`}>
          <div className="p-6">
            <h2 className="text-xl font-bold text-blue-600 mb-8">CzarCore</h2>
            <nav className="space-y-2">
              {[
                { id: 'dashboard', label: 'Dashboard' },
                { id: 'holidays', label: 'Holiday Calendar' }
              ].map((item) => (
                <button
                  key={item.id}
                  onClick={() => setActiveTab(item.id)}
                  className={`w-full text-left px-4 py-3 rounded-lg transition-colors ${
                    activeTab === item.id
                      ? 'bg-indigo-600 text-white'
                      : theme === 'dark'
                      ? 'text-gray-300 hover:bg-gray-700'
                      : 'text-gray-600 hover:bg-gray-100'
                  }`}
                >
                  {item.label}
                </button>
              ))}
            </nav>
          </div>
          <div className="absolute bottom-4 left-4 right-4">
            <button
              onClick={toggleTheme}
              className={`w-full p-3 rounded-lg transition-colors ${
                theme === 'dark'
                  ? 'bg-gray-700 text-yellow-400 hover:bg-gray-600'
                  : 'bg-gray-200 text-gray-600 hover:bg-gray-300'
              }`}
            >
              {theme === 'dark' ? '☀️' : '🌙'} Theme
            </button>
          </div>
        </div>

        <div className="ml-64 flex-1 p-8">
          <div className="flex justify-between items-center mb-8">
            <h1 className="text-3xl font-bold">
              {activeTab === 'dashboard' && 'Admin Dashboard'}
              {activeTab === 'holidays' && 'Holiday Calendar'}
            </h1>
            <button
              onClick={() => {
                localStorage.removeItem('user');
                localStorage.removeItem('token');
                window.location.href = '/auth';
              }}
              className="px-6 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
            >
              Logout
            </button>
          </div>

          {activeTab === 'dashboard' && (
            <div className={`${theme === 'dark' ? 'bg-gray-800' : 'bg-white'} p-8 rounded-xl shadow-lg`}>
              <h2 className="text-2xl font-bold mb-4">Welcome to Admin Dashboard</h2>
              <p className="text-gray-600 mb-6">Manage holidays and system settings</p>
              <button 
                onClick={() => setActiveTab('holidays')} 
                className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
              >
                Manage Holidays
              </button>
            </div>
          )}

          {activeTab === 'holidays' && (
            <ProfessionalCalendar
              holidays={holidaysArray}
              onAddHoliday={async (holidayData) => {
                try {
                  const response = await fetch('http://localhost:5002/api/holidays', {
                    method: 'POST',
                    headers: {
                      'Content-Type': 'application/json',
                    },
                    body: JSON.stringify(holidayData),
                  });
                  if (response.ok) {
                    fetchHolidays();
                  }
                } catch (error) {
                  console.error('Error adding holiday:', error);
                }
              }}
              onDeleteHoliday={handleHolidayDelete}
              theme={theme}
            />
          )}
        </div>
      </div>
    </div>
  );
}

export default AdminPanel;
